_Tb_cfg_hero = {
[100001] = { '献帝','',1,3,25,1,3,3500,8100,8100,4100,900,38,114,79,45,41,200001,9,'1,10;3,5;4,12;',4,'3;99;99;2;','汉灵帝刘宏和王美人所生之子。董卓废汉少帝刘辩后，立刘协为皇帝。董卓被诛后，曹操控制了刘协，挟天子以令诸侯。\n曹操死后，刘协被曹丕控制，随后被迫禅让于曹丕。',0,10, },
[100002] = { '十常侍','',1,4,15,1,2,5000,4200,8000,7800,900,63,53,110,132,57,200002,9,'1,12;3,5;4,13;',4,'4;99;99;2;','汉灵帝时的宦官集团，人称“十常侍”，其首领是张让和赵忠。他们仗着皇帝的宠信，独断专权，使得朝纲腐败。\n在黄巾军起义后，和大将军何进对立，企图谋杀何进，后来在袁绍攻入都城时全部被诛。',0,1, },
[100003] = { '吕布','',1,4,35,3,3,9900,10000,3000,10000,300,230,189,30,202,26,200003,9,'1,5;3,13;4,20;',6,'4;99;99;2;','其人骁勇善战，曾在虎牢关力战三英（刘备、关羽、张飞），且一人可独斗曹军六员大将，被视为三国最强武将，人称“飞将军”。\n虽然勇猛，但是少有计策，为人反复无常，唯利是图，最终不敌曹操和刘备的联军，兵败人亡。',0,5, },
[100004] = { '蔡文姬','',1,4,20,3,2,6100,7200,8100,8500,200,82,80,118,95,14,200004,9,'1,5;3,13;4,20;',6,'4;99;99;2;','东汉末才女，博学多才且精通音律。\n匈奴入侵时，被匈奴掳走。十二年后，曹操统一北方，用重金将蔡文姬赎回，并将其嫁给董祀。',1,10, },
[100005] = { '貂蝉','',1,4,25,1,2,6700,7200,8800,6800,1000,97,87,160,107,72,200005,9,'1,12;3,5;4,13;',4,'4;99;99;2;','【中国古代四大美女之一】。其善歌舞，色伎俱佳，凭借自己的美貌智慧在诸侯争霸的战乱年代，辗转于各诸侯之间。\n她通过王允的安排成为了吕布的妾室，巧施连环计成功诱使吕布诛杀了董卓。',1,5, },
[100006] = { '袁绍','',1,4,30,2,2,8200,8800,8000,3600,2500,130,163,105,57,196,200006,9,'1,7;3,10;4,13;',4,'4;99;99;2;','出身袁氏名门，和曹操是小时候的同学。后来成为反董卓联盟的盟主。在董卓死后，利用其名门旺族的实力，在河北迅速取得强大的势力。\n后来在夺取天下的关键一役—官渡之战中大败于曹操，最后在郁郁寡欢中病死。',0,10, },
[100007] = { '卢植','',1,4,30,3,1,8100,9200,8800,8900,700,128,183,151,153,67,200007,9,'1,5;3,13;4,20;',6,'4;99;99;2;','东汉名臣，是刘备和公孙瓒之师。奉命镇压黄巾军时，以三万精锐，包围了张角的十五万人马，未见胜负，因拒绝贿赂宦官，而被诬陷罢职问罪。\n董卓得政后，卢植又复职，谏阻董卓废陈留王立献帝。',0,10, },
[100008] = { '张角','',5,4,25,3,2,2100,6700,9300,8500,300,29,92,215,157,25,200008,11,'1,7;3,17;4,25;',7,'4;99;99;2;','黄巾军的领袖，太平道的创始人。自称受到南华老仙的指点，被授予“太平要术”，具有超凡的神力。\n以“天公将军”之名，带着数百万的信徒，企图推翻大汉的统治。起义持续了数月，虽然最后被镇压，但是却成了大汉皇朝灭亡的导火索。',0,10, },
[100009] = { '董卓','',5,4,30,3,1,8800,9100,6800,8100,1000,207,230,79,164,101,200009,11,'1,7;3,17;4,25;',7,'4;99;99;2;','使东汉王朝走向灭亡的诸侯。在灵帝末年的十常侍之乱时受大将军何进之召率军进京，旋即掌控朝中大权。其为人残忍嗜杀，倒行逆施，招致群雄联合讨伐。后被其亲信吕布所杀，余部由李傕等人率领。',0,10, },
[100010] = { '吕布','',5,4,40,3,3,10800,9200,2800,10000,300,395,242,39,242,33,200010,11,'1,7;3,17;4,25;',7,'4;99;99;2;','其人骁勇善战，曾在虎牢关力战三英（刘备、关羽、张飞），且一人可独斗曹军六员大将，被视为三国最强武将，人称“飞将军”。\n虽然勇猛，但是少有计策，为人反复无常，唯利是图，最终不敌曹操和刘备的联军，兵败人亡。',0,3, },
[100011] = { '贾诩','',5,4,30,2,3,5200,8100,9800,3400,1300,96,164,248,45,144,200011,11,'1,9;3,13;4,17;',5,'4;99;99;2;','曹魏初年著名谋士，也是曹魏的开国功臣。张绣曾用他的计策两次打败曹操，官渡之战前他劝张绣归降曹操。\n在曹操与关中联军相持渭南时，贾诩献离间计瓦解马超、韩遂，使得曹操一举平定关中。',0,10, },
[100012] = { '吕布','',5,4,35,1,4,10000,9900,3100,8000,300,221,257,46,147,30,200012,11,'1,15;3,7;4,17;',9,'4;99;99;2;','其人骁勇善战，曾在虎牢关力战三英（刘备、关羽、张飞），且一人可独斗曹军六员大将，被视为三国最强武将，人称“飞将军”。\n虽然勇猛，但是少有计策，为人反复无常，唯利是图，最终不敌曹操和刘备的联军，兵败人亡。',0,0, },
[100013] = { '马超','',5,4,30,3,3,9700,9100,4500,9200,400,278,184,52,154,41,200013,11,'1,7;3,17;4,25;',7,'4;99;99;2;','蜀国五虎将之一，以勇猛着称，早年随父征战各地。\n曹操诱杀马腾后，马超为报父仇，起兵反曹，在潼关击败大将张郃，数合枪挑李通，杀得曹操割须弃袍，夏侯渊、曹洪、许褚三人的夹攻下，仍然被马超杀至壕边。',0,5, },
[100014] = { '董卓','',1,4,35,3,2,9400,8800,5400,7000,1400,191,101,94,122,146,200100,9,'1,5;3,13;4,20;',10,'4;99;99;2;','为演义中的虚构人物。黄巾军的将军，张宝的部下。在黄巾之乱中，率领黄巾军四处叛乱。\n后来遇到关羽，被关羽的人格魅力所折服，在黄巾军被平定后成为了关羽的部下。',0,0, },
[100015] = { '甄洛','',5,4,20,1,3,6500,7500,9000,4200,1000,131,129,88,85,94,200015,11,'1,15;3,7;4,17;',5,'4;99;99;2;','初期嫁与袁绍次子袁熙，官渡之战时，袁氏败亡，曹军攻入城内，曹丕见其美艳动人，便纳为己有，后来生下曹睿。在曹丕称帝后被封为皇后。',1,10, },
[100016] = { '刘备','',3,4,35,2,3,8000,8100,8000,3800,1800,154,183,128,92,173,200016,10,'1,8;3,11;4,15;',4,'4;99;99;2;','西汉中山靖王之后，三国时期蜀汉开国皇帝。为人弘毅宽厚，知人待士，百折不挠。与张飞、关羽桃园结义，征战四方，终成霸业。',0,5, },
[100017] = { '诸葛亮','',3,4,30,2,2,4900,9500,10800,2200,2100,79,167,286,39,201,200017,10,'1,8;3,11;4,15;',4,'4;99;99;2;','受刘备三顾茅庐之邀而成为刘备的军师。其一生“鞠躬尽瘁、死而后已”，为蜀国的军事和政治作出了卓越的贡献。是中国传统文化中忠臣与智者的代表人物，\n其火烧博望与新野、七擒孟获、六出祁山等事迹均为其著名战例。',0,5, },
[100018] = { '刘备','',3,4,25,3,2,7000,7900,8400,8300,500,122,160,124,145,53,200100,10,'1,6;3,15;4,22;',7,'4;99;99;2;','西汉中山靖王之后，三国时期蜀汉开国皇帝。为人弘毅宽厚，知人待士，百折不挠。与张飞、关羽桃园结义，征战四方，终成霸业。',0,0, },
[100019] = { '马云禄','',3,4,25,3,3,8900,7800,5500,8800,300,156,114,57,142,24,200019,10,'1,6;3,15;4,22;',7,'4;99;99;2;','民国作家虚构的人物。马超之妹，自幼习武，女中无双。在为马腾复仇的战事中跟随马超来到中原，与赵云一见钟情。\n之后嫁于赵云，与丈夫和兄长并肩作战。',1,10, },
[100020] = { '黄月英','',3,4,25,2,2,3300,7000,9400,2900,1600,53,113,190,47,181,200020,10,'1,8;3,11;4,15;',4,'4;99;99;2;','诸葛亮之妻。其上通天文，下察地理，韬略近于诸书无所不晓。\n相传诸葛亮发明的“木牛流马”和“机关人”是从黄月英的传授的技巧上发展出来。',1,10, },
[100021] = { '赵云','',3,4,35,2,3,10100,9200,7800,6500,1300,227,223,112,114,138,200021,10,'1,8;3,11;4,15;',4,'4;99;99;2;','蜀汉“五虎上将”之一。其人姿颜雄伟，勇武过人。先后参加过博望坡之战、长坂坡之战、江南平定战，独自指挥过入川之战、汉水之战、箕谷之战，均屡获奇功。',0,5, },
[100022] = { '张飞','',3,4,35,3,2,10300,8700,4500,7100,1000,267,168,50,113,96,200022,10,'1,6;3,15;4,22;',7,'4;99;99;2;','蜀国名将，与刘备、关羽是“桃园结义”的兄弟。其人以勇猛、鲁莽、嫉恶如仇而著称。\n曾于长板桥仅率二十骑断后，据水断桥，百万曹军无一人敢近身。',0,5, },
[100023] = { '曹操','',2,4,35,3,2,8000,10700,9000,8800,600,128,295,159,126,40,200023,10,'1,6;3,15;4,22;',7,'4;99;99;2;','三国中曹魏政权的缔造者。其文武皆通，被誉为乱世之奸雄。\n以汉天子的名义征讨四方，对内消灭二袁、吕布、刘表、马超、韩遂等割据势力，对外降服南匈奴、乌桓、鲜卑等，统一了中国北方，并实行一系列政策恢复经济生产和社会秩序，奠定了曹魏立国的基础。',0,5, },
[100024] = { '荀彧','',2,4,30,3,2,4700,8900,9600,8500,500,55,156,225,149,44,200024,10,'1,6;3,15;4,22;',7,'4;99;99;2;','曹操统一北方的首席谋臣和功臣，具有经天纬地之才，被称为“王佐之才”。\n在建计、密谋、匡弼、举人多有建树，被曹操称为“吾之子房”。',0,10, },
[100025] = { '曹丕','',2,4,25,1,4,8300,7800,8300,4500,1100,145,126,145,79,97,200025,10,'1,13;3,6;4,15;',4,'4;99;99;2;','曹操次子。其文武双全，八岁能提笔为文，善骑射，好击剑，博览古今经传，通晓诸子百家学说。曹操去世后，曹丕继任丞相、魏王。之后逼献帝禅让成为文帝，以魏代汉，结束了汉朝四百多年的统治。\n在位期间，平定边患。击退鲜卑，和匈奴、氐、羌等外夷修好，恢复汉朝在西域的设置。并执行了多个优良政策，重用贤臣，使魏国安定。但时年40岁，病逝。',0,10, },
[100026] = { '关羽','',2,4,30,3,1,10200,8600,6200,9000,300,240,177,62,131,27,200026,10,'1,6;3,15;4,22;',7,'4;99;99;2;','与刘备、张飞桃园结义。与张飞一同被称为万人敌。其“温酒斩华雄”、“斩颜良诛文丑”、“过五关斩六将”等英勇事迹被后人所传颂。\n在关羽去世后，其形象逐渐被神化，一直是历来民间祭祀的对象，被称为“关公”。',0,5, },
[100027] = { '张辽','',2,4,35,3,3,9100,9400,8300,10500,300,221,227,105,272,29,200027,10,'1,6;3,15;4,22;',7,'4;99;99;2;','曹魏名将。曾从属丁原、董卓、吕布。下邳之战后，归顺曹操。此后随曹操征讨，战功累累。\n在逍遥津之战，更是以七千之众大破十万大军，差点活捉孙权，经此一役，张辽威震江东，声名大噪，成为历代推崇的名将之一，“张辽止啼”也成为民间流传的传奇故事。',0,5, },
[100028] = { '王异','',2,4,25,1,4,8800,5200,9000,5300,900,142,84,170,86,72,200028,10,'1,13;3,6;4,15;',4,'4;99;99;2;','曹魏益州刺史赵昂之妻，以贞烈、聪明而闻名的巾帼女将。\n马超作乱凉州时，王异协助丈夫守城，多有功勋，自马超攻冀城至祁山坚守，赵昂曾出奇计九条，王异皆有参与。',1,10, },
[100029] = { '张春华','',2,4,25,1,3,7700,5600,8200,5500,900,124,75,155,104,102,200029,10,'1,13;3,6;4,15;',4,'4;99;99;2;','司马懿之妻。司马懿当初拒曹操之召而以己有风痹推辞，被一婢女得知后，她担心此事泄露招致灾祸，便亲手杀婢女灭口，由此得到司马懿的看重。',1,10, },
[100030] = { '孙权','',4,4,30,1,4,7600,8800,8500,5800,1000,122,128,162,111,113,200030,10,'1,13;3,6;4,15;',4,'4;99;99;2;','自幼文武双全，早年随父兄征战天下。后继承孙坚与孙策的遗志，统领江东。在曹丕与刘备各自称帝时，被魏文帝曹丕封为吴王，建立吴国。之后脱离魏国，并自称吴帝。',0,5, },
[100031] = { '周瑜','',4,4,30,1,4,7000,9200,9800,5300,1200,123,134,230,93,105,200031,10,'1,13;3,6;4,15;',4,'4;99;99;2;','孙吴名将，其人长壮有姿貌，且具经天纬地之才。在赤壁之战中起了决定性的作用，大败曹军，由此奠定了三分天下的基础。',0,5, },
[100032] = { '周姬','',4,4,20,1,2,2700,5000,8500,3700,1200,34,88,159,46,89,200032,10,'1,13;3,6;4,15;',4,'4;99;99;2;','周瑜与小乔所生之女。由于其父母都是吴国有数的俊男美女，因此周姬本身也是绝世美女，同时也继承了周瑜的才智。后成为孙权之子孙登之妻。',1,10, },
[100033] = { '孙坚','',4,4,35,2,3,9000,10000,7200,3600,2000,189,209,80,57,211,200033,10,'1,8;3,11;4,15;',4,'4;99;99;2;','孙策和孙权之父。为“十八路诸侯反董卓”中的一路，表现相当出色。\n后董卓迁都长安，孙坚进驻洛阳，在井中发现了玉玺，从而坚信自己会成为天下霸主，却在与黄祖的战役中被流矢射中而亡。',0,10, },
[100034] = { '甘宁','',4,4,30,2,2,9600,8700,6700,4600,1400,211,192,78,67,135,200034,10,'1,8;3,11;4,15;',4,'4;99;99;2;','吴国猛将。性格粗暴，年轻时经常为非作歹，行为举止旁若无人。后来熟读诸子而被感化，投靠刘表和黄祖，但均未被重用。\n于是甘宁率步众投奔孙权，破黄祖据楚关，攻曹仁取夷陵，镇益阳拒关羽，守西陵获朱光，率骑袭曹营，战功赫赫。',0,10, },
[100035] = { '吕蒙','',4,4,35,1,4,8600,9200,9300,5400,900,166,223,164,87,87,200648,10,'1,13;3,6;4,15;',4,'4;99;99;2;','孙吴重臣。早年果敢有胆，后折节读书，识见精博，终成大将，有“士别三日，更应刮目相待”之语。\n曾在逍遥津之战中奋勇抵抗张辽军追袭，并于濡须数御魏军；又设计袭取荆州，击败关羽，屡获战功。',0,5, },
[100036] = { '孙尚香','',4,4,25,1,4,9000,7700,6200,5900,900,170,135,57,79,72,200035,10,'1,13;3,6;4,15;',4,'4;99;99;2;','孙权之妹。为了巩固吴蜀同盟关系而嫁给了刘备。为一巾帼英雄，在勇武上也不输给一般武将。\n虽然是政治联姻，但后来被刘备的英雄气概深深折服而爱上刘备。听闻刘备病逝时，望西痛哭，投江而死。',1,10, },
[100037] = { '何太后','',1,4,25,1,3,6800,6700,8100,5300,1000,82,81,137,64,72,200037,9,'1,12;3,5;4,13;',4,'4;99;99;2;','何进同父异母的妹妹。相貌甜美，被灵帝招入后宫，生下了刘辩。\n灵帝死后，为了让刘辩继位，毒死了献帝的母亲王美人，后来和刘辩一起被进京的董卓所杀。',1,10, },
[100038] = { '王美人','',1,3,20,3,2,6000,7800,8300,8600,300,67,70,101,86,15,200038,9,'1,5;3,12;4,17;',6,'3;99;99;2;','汉献帝刘协之母。其才色兼备，深受汉灵帝刘宏的宠爱。后在皇位继承人的争夺中，被何太后毒死。',1,3, },
[100039] = { '皇甫嵩','',1,3,25,3,2,7600,9300,8200,8900,700,82,101,80,96,41,200039,9,'1,5;3,12;4,17;',6,'3;99;99;2;','为人仁爱谨慎，尽忠职守，有谋略，有胆识。在讨伐黄巾军的战斗中，击杀了张梁，并屡获战功。',0,10, },
[100040] = { '朱儁','',1,3,25,1,4,8900,8300,7500,4800,1200,96,90,65,52,70,200040,9,'1,10;3,5;4,12;',4,'3;99;99;2;','和皇甫嵩一起镇压了各地的黄巾军起义。由于镇压有功，被授予车骑将军。\n后董卓余党李傕、郭汜乱政，掠夺公卿，朱儁对汉室衰微的状况感到悲哀，归家后即病逝。',0,10, },
[100041] = { '张奂','',1,3,20,2,2,7800,7900,8800,3300,2000,78,79,97,30,108,200041,9,'1,6;3,9;4,12;',4,'3;99;99;2;','“凉州三明”之一。在东汉对外战争中功勋卓著，多次以恩信安抚、招降外族，使得北方宁静一时。\n后入朝，为宦官所利用，率军前往进击窦武。事后自责不已，拒受封侯。',0,10, },
[100042] = { '曹操','',1,3,25,3,2,8200,8800,8500,9300,200,80,95,83,90,11,200042,9,'1,5;3,12;4,17;',6,'3;99;99;2;','三国中曹魏政权的缔造者。其文武皆通，被誉为乱世之奸雄。\n以汉天子的名义征讨四方，对内消灭二袁、吕布、刘表、马超、韩遂等割据势力，对外降服南匈奴、乌桓、鲜卑等，统一了中国北方，并实行一系列政策恢复经济生产和社会秩序，奠定了曹魏立国的基础。',0,10, },
[100043] = { '孙坚','',1,3,25,3,2,9000,8200,6200,9200,600,107,89,47,100,31,200043,9,'1,5;3,12;4,17;',6,'3;99;99;2;','孙策和孙权之父。为“十八路诸侯反董卓”中的一路，表现相当出色。\n后董卓迁都长安，孙坚进驻洛阳，在井中发现了玉玺，从而坚信自己会成为天下霸主，却在与黄祖的战役中被流矢射中而亡。',0,10, },
[100044] = { '刘备','',1,3,25,2,3,7500,8100,7200,4000,1500,73,79,62,39,97,200044,9,'1,6;3,9;4,12;',4,'3;99;99;2;','西汉中山靖王之后，三国时期蜀汉开国皇帝。为人弘毅宽厚，知人待士，百折不挠。与张飞、关羽桃园结义，征战四方，终成霸业。',0,10, },
[100045] = { '鲍信','',1,3,20,1,3,5400,5300,8700,6100,1100,54,53,78,61,60,200100,9,'1,10;3,5;4,12;',4,'3;99;99;2;','东汉末年济北相，讨伐董卓的诸路人马之一。在与黄巾军交战期间，为救曹操不幸战死。',0,0, },
[100046] = { '董姬','',5,3,20,3,1,6400,5200,7000,9600,600,82,67,90,123,37,200046,11,'1,6;3,15;4,22;',7,'3;99;99;2;','董卓的孙女。在幼年时就深受祖父董卓的溺爱，在娇生惯养的环境中长大。董卓被诛，灭三族时被处死。',1,10, },
[100047] = { '严氏','',5,3,20,3,2,9000,6300,6700,7500,100,114,81,86,106,6,200649,11,'1,6;3,15;4,22;',7,'3;99;99;2;','吕布之妻。在曹操攻打下邳时，陈宫向吕布建议出城包抄敌人后方。但严氏强烈反对吕布将妻儿留在城内，致使吕布不听陈宫计策，后败于曹操。',1,10, },
[100048] = { '邹氏','',5,3,20,2,1,5900,7900,5500,3000,1600,76,101,71,35,110,200047,11,'1,8;3,11;4,15;',4,'3;99;99;2;','张济之妻，张绣之婶。张绣投降曹操后，操侄曹安民引夫人见操，操见夫人美貌，而强纳之。',1,5, },
[100049] = { '华雄','',5,3,30,2,2,8200,8600,4100,3200,1900,124,143,49,44,136,200048,11,'1,8;3,11;4,15;',4,'3;99;99;2;','董卓帐下都督。身长九尺，虎背熊腰豹头，相貌可怖。数破反董卓联军，接连斩杀对方的得力武将，使得联军大乱。',0,10, },
[100050] = { '张辽','',5,3,30,3,1,9200,8800,6300,9800,400,124,133,75,132,29,200049,11,'1,6;3,15;4,22;',7,'3;99;99;2;','曹魏名将。曾从属丁原、董卓、吕布。下邳之战后，归顺曹操。此后随曹操征讨，战功累累。\n在逍遥津之战，更是以七千之众大破十万大军，差点活捉孙权，经此一役，张辽威震江东，声名大噪，成为历代推崇的名将之一，“张辽止啼”也成为民间流传的传奇故事。',0,10, },
[100051] = { '张梁','',5,3,30,2,2,8100,7900,7700,3200,1600,122,107,92,44,100,200053,11,'1,8;3,11;4,15;',4,'3;99;99;2;','黄巾起义首领之一，张角的三弟。自称人公将军。遭到朝廷所派左中郎将皇甫嵩进攻时，他率军在广宗进行反击。后因警戒疏忽，遭到汉军夜袭，兵败身亡。',0,10, },
[100052] = { '管亥','',5,3,25,3,2,9100,7000,3300,8400,500,126,97,32,115,33,200051,11,'1,6;3,15;4,22;',7,'3;99;99;2;','黄巾军的将领。孔融屯兵都昌时，他率军围攻孔融，一合斩杀孔融的大将宗宝，后与关羽大战数十合后被斩。',0,5, },
[100053] = { '徐荣','',5,3,25,2,2,8000,8500,6600,3200,1600,111,117,63,40,105,200052,11,'1,8;3,11;4,15;',4,'3;99;99;2;','董卓的勇将。受董卓之命讨伐曹操和袁绍结成的反董卓联盟。\n徐荣按照李儒的计策，击败了曹操。并和反扑的夏侯惇交战，平分秋色。后战死于新丰之战。',0,5, },
[100054] = { '张曼成','',5,2,25,2,2,8200,7500,6000,2400,1900,113,104,57,30,109,200206,11,'1,6;3,9;4,12;',4,'2;99;99;2;','南阳黄巾军的首领，自称“神上使”。在南阳集结民众响应黄巾军起义而发兵。\n南阳沦陷之后，将宛县城作为据点继续领导黄巾军。',0,10, },
[100055] = { '吕姬','',5,3,20,2,2,9200,6600,5000,2200,1500,142,76,44,29,103,200054,11,'1,8;3,11;4,15;',4,'3;99;99;2;','吕布与严氏之女。她继承了父之武勇，和母之美貌。虽与袁术之子曾有政治婚姻之提议，但于前往寿春途中被带回。',1,5, },
[100056] = { '左慈','',5,2,20,1,2,3200,4400,9900,5200,900,29,39,88,46,48,200197,11,'1,10;3,5;4,12;',4,'2;99;99;2;','神秘的仙人，曾说刘备将夺取天下而激怒曹操。曹操于是命人逮捕了数百与左慈容貌相同的人。\n这些人被斩首时从头部冒出的青烟变成了驾鹤的左慈，左慈预言曹操的死期之后消失。',0,10, },
[100057] = { '田丰','',5,3,25,3,1,5000,8400,9100,7900,400,55,128,152,98,27,200056,11,'1,6;3,15;4,22;',7,'3;99;99;2;','博学多才富于谋略，为袁绍的军师。在和公孙瓒的战斗中，利用其谋略帮助袁绍军取得胜利。\n后因谏阻袁绍征伐曹操而被袁绍下令监禁。官渡之战后，田丰被袁绍杀害。',0,10, },
[100058] = { '颜良','',5,3,25,3,1,9200,7400,5200,9500,200,153,102,50,131,14,200057,11,'1,6;3,15;4,22;',7,'3;99;99;2;','袁绍靡下猛将，与文丑以勇武齐名。在和曹操的战斗中担任先锋，一举击杀曹操军的魏续和宋宪，甚至击败了前来救援的徐晃。\n后与在曹营中的关羽交战时被斩杀。',0,5, },
[100059] = { '文丑','',5,3,25,2,2,9100,8900,5000,2300,1500,126,148,48,29,99,200058,11,'1,8;3,11;4,15;',4,'3;99;99;2;','袁绍帐下的大将。和颜良齐名，曾和赵云单挑而平分秋色。\n在和曹军的战争中，曾击败张辽和徐晃，但在延津中了曹操的诱敌之计，在混乱中被关羽斩杀。',0,5, },
[100060] = { '吕姬','',5,3,20,3,2,8600,6800,4500,8600,200,133,78,40,133,13,200059,11,'1,6;3,15;4,22;',7,'3;99;99;2;','吕布与严氏之女。她继承了父之武勇，和母之美貌。虽与袁术之子曾有政治婚姻之提议，但于前往寿春途中被带回。',1,1, },
[100061] = { '董卓','',5,3,30,3,1,8900,9300,6500,8100,700,134,140,98,122,70,200100,11,'1,6;3,15;4,22;',7,'3;99;99;2;','使东汉王朝走向灭亡的诸侯。在灵帝末年的十常侍之乱时受大将军何进之召率军进京，旋即掌控朝中大权。其为人残忍嗜杀，倒行逆施，招致群雄联合讨伐。后被其亲信吕布所杀，余部由李傕等人率领。',0,0, },
[100062] = { '马腾','',5,3,25,3,3,9100,7600,6200,9300,400,113,105,59,128,27,200061,11,'1,6;3,15;4,22;',7,'3;99;99;2;','董卓乱政时，马腾为西凉太守，起兵应诏，为十八路诸侯之一。马超、马休、马铁之父。\n曾两次试图刺杀曹操，但均被识破，之后被处决。',0,5, },
[100063] = { '甘皇后','',3,3,20,2,2,3500,5200,8600,2100,1500,39,57,96,24,90,200062,10,'1,7;3,10;4,13;',4,'3;99;99;2;','刘备之妻，刘禅之母。她一直尽心尽力地为刘备处理各种内事。虽是小妾，但一直以来都被刘备作为正房看待。在赤壁之战后不幸早逝。',1,5, },
[100064] = { '糜夫人','',3,3,15,1,4,6500,4800,8000,4100,1100,68,51,84,43,62,200650,10,'1,11;3,5;4,13;',4,'3;99;99;2;','刘备之妻，糜竺之妹，是个贤内助。长坂兵败，她怀抱年仅两岁的刘禅在乱军中走散，被赵云发现，因为赵云只有一匹马而不肯上马，在将阿斗托付给赵云后投井而亡。',1,10, },
[100065] = { '关羽','',3,3,30,2,2,9700,9000,7000,3700,1600,127,130,73,44,113,200063,10,'1,7;3,10;4,13;',4,'3;99;99;2;','与刘备、张飞桃园结义。与张飞一同被称为万人敌。其“温酒斩华雄”、“斩颜良诛文丑”、“过五关斩六将”等英勇事迹一直被后人所传颂。\n在关羽去世后，其形象逐渐被神化，一直是历来民间祭祀的对象，被称为“关公”。',0,5, },
[100066] = { '魏延','',3,3,25,2,1,9300,8500,6600,3200,1600,112,102,55,35,103,200064,10,'1,7;3,10;4,13;',4,'3;99;99;2;','蜀汉名将，深受刘备器重。在刘备攻汉中，和诸葛亮北伐的战争中功绩显著，是一名独当一方的大将。\n但其性格过于骄傲自负，刘备死后和诸葛亮产生嫌隙，奇袭长安的计策也未被采用，因而心怀不满。\n与长史杨仪不和，诸葛亮死后，两人矛盾激化，相互争权，魏延败逃，为马岱所追斩。',0,10, },
[100067] = { '徐庶','',3,3,30,3,1,8000,8200,9700,8800,400,105,108,153,114,25,200065,10,'1,5;3,13;4,19;',6,'3;99;99;2;','刘备屯驻新野时，徐庶前往投奔，并向刘备推荐诸葛亮。精通兵法，曾破了曹仁的“八门金锁”阵，赢得了新野之战的胜利。\n因母亲被曹操所掳获，徐庶不得已辞别刘备，加入曹营。由于其“身在曹营心在汉”，因此终身也没有向曹魏献过一策。',0,5, },
[100068] = { '张飞','',3,3,30,2,1,9900,7800,3500,2800,1700,156,102,32,33,120,200066,10,'1,7;3,10;4,13;',4,'3;99;99;2;','蜀国名将，与刘备、关羽是“桃园结义”的兄弟。其人以勇猛、鲁莽、嫉恶如仇而著称。\n曾于长板桥仅率二十骑断后，据水断桥，百万曹军无一人敢近身。',0,10, },
[100069] = { '庞统','',3,3,25,2,2,5900,7100,9800,2300,1800,71,85,142,25,115,200067,10,'1,7;3,10;4,13;',4,'3;99;99;2;','被称为凤雏，与诸葛亮齐名，为刘备两大军师之一。帮助刘备讨伐刘璋，立下赫赫功绩。可惜在落凤坡中伏身死。',0,10, },
[100070] = { '穆皇后','',3,3,20,3,1,6200,8500,6800,8600,600,76,113,68,96,36,200068,10,'1,5;3,13;4,19;',6,'3;99;99;2;','吴懿的妹妹。本为刘璋之兄刘瑁的妻子，但其丈夫早逝，年轻时就成了寡妇。刘备入蜀后嫁于刘备。在刘备称帝后成为皇后。',1,5, },
[100071] = { '马姬','',3,3,30,3,1,7800,8100,5500,8600,300,102,139,50,113,17,200069,10,'1,5;3,13;4,19;',6,'3;99;99;2;','马超之女。继承了父亲的勇敢刚强，马术精湛。\n之后嫁给了刘备之子，平安王刘理，成为贤内助，给予丈夫很大的帮助。',1,1, },
[100072] = { '关银屏','',3,4,30,2,2,8000,8400,6300,3300,1500,152,185,63,43,117,200070,10,'1,8;3,11;4,15;',4,'4;99;99;2;','关羽的三女，也称为关凤。继承了父亲的武勇，虽为女性，却南蛮讨伐战中表现出色。\n孙权欲以己子娶关羽之女以结为亲家，不料被关羽“虎女安能配犬子乎”所拒，令蜀国和吴国的关系恶化。',1,10, },
[100073] = { '马谡','',3,2,20,2,2,5500,5800,8300,2400,1600,43,45,64,19,74,200120,10,'1,5;3,8;4,10;',4,'2;99;99;2;','随兄马良仕刘备入蜀。刘备临终对诸葛亮说：“马谡言过其实，不可大用，您要谨慎！”诸葛亮并未听取，以马谡为心腹。\n在街亭之战中，因不听从诸葛亮的作战指令，导致街亭失守，后被诸葛亮将他斩首以正军法。',0,10, },
[100074] = { '姜维','',3,4,25,2,2,9200,8900,9100,2900,1900,161,168,121,39,136,200072,10,'1,8;3,11;4,15;',4,'4;99;99;2;','诸葛亮的爱徒，人称麒麟儿，文武双全。\n诸葛亮去世后姜维在蜀汉开始崭露头角，费祎死后开始独掌军权，继续率领蜀汉军队北伐曹魏，但未有较大成果。',0,10, },
[100075] = { '郭皇后','',2,3,20,1,4,5000,4200,8500,3600,1700,39,52,113,40,90,200073,10,'1,11;3,5;4,13;',4,'3;99;99;2;','曹魏王朝第一位皇后。其生性节俭，不事享乐，并且貌美，深得曹丕宠爱。',1,10, },
[100076] = { '卞夫人','',2,3,20,3,1,3200,4100,7000,9000,600,36,46,78,100,32,200074,10,'1,5;3,13;4,19;',6,'3;99;99;2;','魏武宣皇后卞氏，曹操之妻，曹丕、曹彰、曹植之母。出生歌妓，后得曹操赏识娶其为妻。\n其性格聪明谨慎，即使成为皇后也不贪恋权势，是曹操的内助之贤。',1,10, },
[100077] = { '丁夫人','',2,3,15,2,2,5000,7200,6600,2400,1900,53,76,48,23,107,200075,10,'1,7;3,10;4,13;',4,'3;99;99;2;','曹操的正室，由于刘夫人早逝，曹操便让没有生子的丁夫人抚育其子曹昂，丁夫人将曹昂视为己出。\n后来曹昂在宛城战死，丁夫人悲痛欲绝，常常数落曹操，曹操一怒之下，将她打发回娘家。',1,5, },
[100078] = { '郭嘉','',2,3,25,3,3,5000,6500,10000,8500,600,48,70,157,102,35,200076,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曹操早期军事智囊团的核心人物，被誉为曹营第一谋士。外人常将他与姜子牙相比，外号“小太公”。史书上称他“才策谋略，世之奇士”。',0,5, },
[100079] = { '夏侯惇','',2,3,30,3,1,8900,9200,7600,8300,300,105,121,69,98,19,200077,10,'1,5;3,13;4,19;',6,'3;99;99;2;','从曹操白手起家时就跟随的勇将。平时清廉谨慎，但一到战场上就如鬼神般勇猛。在讨伐吕布时被箭射中左眼，仍然勇猛难挡。\n多次为曹操镇守后方，屡获功绩，官拜大将军。',0,3, },
[100080] = { '许褚','',2,3,30,2,2,9700,7200,4500,3200,1700,166,94,41,38,120,200078,10,'1,7;3,10;4,13;',4,'3;99;99;2;','曹操靡下猛将，容貌雄毅，勇力绝人，但平时呆头呆脑，因而被称为虎痴。\n跟随曹操征讨张绣，于官渡与袁绍对峙，围邺城等以勇武著称，屡获战功，深受曹操的重用。',0,10, },
[100081] = { '典韦','',2,3,30,2,1,9300,9200,3300,2900,1800,122,145,30,34,127,200079,10,'1,7;3,10;4,13;',4,'3;99;99;2;','曹操手下猛将，其人相貌魁梧，臂力过人，擅使两枝八十斤重的铁戟，常护曹操左右。\n宛城之战张绣背叛，奇袭曹操时，典韦为保护曹操而独挡叛军，击杀多人，但最终因寡不敌众而力战身亡。',0,10, },
[100082] = { '郝昭','',2,3,25,2,2,7500,9300,7800,2600,1300,81,158,75,28,94,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','魏国都督。身长九尺，猿臂善射，深有谋略。少年从军时，屡立战功，逐渐晋升为杂号将军，后受曹真的推荐镇守陈仓，防御蜀汉。诸葛亮率军几次北伐，均为郝昭所阻。',0,0, },
[100083] = { '张郃','',2,4,25,2,2,9100,9500,8000,2900,1800,159,153,96,35,144,200081,10,'1,8;3,11;4,15;',4,'4;99;99;2;','曹魏“五子良将”之一，在官渡之战中归顺曹操。其戎马一生，以用兵巧变、善列营阵，长于利用地形著称。\n早年随曹操征战四方，屡建战功。曹丕登位后，在街亭大败蜀军，并于江陵破吴，功绩显著。',0,10, },
[100084] = { '庞德','',2,3,30,3,1,7900,9800,7100,9600,200,104,142,74,126,13,200082,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曾是马超的心腹，不过被曹操打败后投诚魏国。\n之后成为一方统帅与蜀国作战，为表决心，甚至将棺材抬上战场，在与关羽作战时被擒，并拒绝归顺蜀国，最终殒身殉节。',0,10, },
[100085] = { '曹仁','',2,3,30,3,2,8200,8800,7000,8700,200,96,163,73,113,13,200083,10,'1,5;3,13;4,19;',6,'3;99;99;2;','三国曹魏名将，官至大司马，封陈侯。其好弓马骑射，少时不修行检，及至长成为大将，则变得严整，奉法守令。从曹操多年，为魏朝立下不少汗马功劳。',0,5, },
[100086] = { '周泰','',4,3,30,2,1,8700,9000,6100,2800,1500,91,167,55,33,106,200084,10,'1,7;3,10;4,13;',4,'3;99;99;2;','孙策平定江东时加入孙策军，随孙策左右，后孙权爱其为人，向孙策请求让周泰跟随自己。\n周泰多次于战乱当中保护孙权的安危，身上受的伤多达几十处，就像在皮肤上雕画一样，吴将朱然、徐盛等因此对其拜服。',0,10, },
[100087] = { '徐盛','',4,3,25,1,4,8300,7600,7300,5700,1100,110,101,70,69,71,200085,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国名将。刘备伐吴时，徐盛跟随陆逊攻下蜀军多处屯营；曹休伐吴时，徐盛在形势不利的情况下以少抗多，成功防御。后曹丕大举攻吴，吴国依徐盛的建议在建业外围筑上围墙，曹丕被迫退走。',0,10, },
[100088] = { '孙坚','',4,3,30,3,1,8900,9300,6700,8600,400,129,109,61,113,25,200086,10,'1,5;3,13;4,19;',6,'3;99;99;2;','孙策和孙权之父。为“十八路诸侯反董卓”中的一路，表现相当出色。\n后董卓迁都长安，孙坚进驻洛阳，在井中发现了玉玺，从而坚信自己会成为天下霸主，却在与黄祖的战役中被流矢射中而亡。',0,10, },
[100089] = { '孙策','',4,3,30,3,2,9500,7100,7100,7600,200,137,93,64,100,13,200087,10,'1,5;3,13;4,19;',6,'3;99;99;2;','人称“江东小霸王”。为继承父亲孙坚的遗业而屈事袁术，后脱离袁术，统一江东。在一次狩猎中为刺客所伤，不久后身亡。',0,3, },
[100090] = { '太史慈','',4,4,25,1,4,9800,8300,6700,6500,800,198,111,62,105,57,200088,10,'1,13;3,6;4,15;',4,'4;99;99;2;','原为刘繇部下，后被孙策收降，自此太史慈为孙氏大将，助其扫荡江东。其弓马熟练，箭法精良。后在合肥之战中战死。',0,10, },
[100091] = { '吴夫人','',4,3,15,3,2,4900,6600,8000,8700,200,41,62,84,91,10,200089,10,'1,5;3,13;4,19;',6,'3;99;99;2;','孙坚之妻，是孙策、孙权生母。才貌双全，性格果敢坚毅，富有才智谋略，先后辅佐两个儿子治理江东，为孙家的霸业做出了卓越的贡献。',1,5, },
[100092] = { '陆逊','',4,3,25,2,2,6800,9600,9900,2500,1900,73,103,143,27,137,200090,10,'1,7;3,10;4,13;',4,'3;99;99;2;','历任吴国大都督、上大将军、丞相。与吕蒙一起在荆州大败关羽；在夷陵用计大败蜀国大军；并几乎将魏国对吴国的侵略全部抵御，可谓居功至伟。',0,5, },
[100093] = { '徐夫人','',4,3,20,1,4,6500,6300,5100,6400,1000,73,70,57,71,60,200651,10,'1,11;3,5;4,13;',4,'3;99;99;2;','孙坚三子孙翊之妻，天生聪慧，精通占卜。\n某日，孙翊要举行酒宴。徐氏为其卜卦，认为此去凶多吉少，劝孙翊改日再设宴，但孙翊不听，最后被刺杀。事后徐夫人成功报仇。',1,5, },
[100094] = { '小乔','',4,3,20,1,2,7700,5300,8100,6100,1000,86,59,100,68,60,200091,10,'1,11;3,5;4,13;',4,'3;99;99;2;','周瑜之妻。与其姊大乔均为绝世美女，并称为“江东二乔”。一直默默地支持着周瑜。',1,5, },
[100095] = { '大乔','',4,3,20,1,3,5700,7500,8200,5400,1100,64,84,101,60,66,200092,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国乔公长女，孙策之妻，和妹妹小乔并称为“江东二乔”，有沉鱼落雁之资，倾国倾城之容。\n曹操赤壁鏖兵，虎视江东，曾有揽二乔娱暮年，还足平生之愿。',1,5, },
[100096] = { '步骘','',4,3,20,2,2,6900,7800,9300,2800,1500,77,87,104,28,90,200093,10,'1,7;3,10;4,13;',4,'3;99;99;2;','吴国重臣，曾向孙权献计利用魏军夺取荆州。其驻守西陵二十年，曹魏边境的将士都敬仰他的威信。他性情宽弘，很得人心，喜怒不形与声色，无论对内还是对外总是表现得十分恭敬。',0,5, },
[100097] = { '蒯良&蒯越','',1,2,40,1,3,6500,6300,8900,5700,900,64,62,87,56,53,200115,9,'1,8;3,4;4,9;',3,'2;99;99;2;','东汉的贤士，蒯越、蒯良兄弟二人一起效力于刘表时，在荆州平定中作出了巨大的贡献。',0,10, },
[100098] = { '何进','',1,2,20,3,2,7900,7300,2700,8300,500,55,51,19,57,21,200110,9,'1,4;3,9;4,14;',5,'2;99;99;2;','何进本为屠夫，因为其妹入宫为皇后，以外戚身份入仕，并成为大将军。\n不过其性格优柔寡断，对于是否杀掉当权宦官，犹豫不决。最后被十常侍之首张让设计所害。',0,10, },
[100099] = { '丁原','',1,2,25,3,1,8000,8500,5900,8900,400,60,64,44,67,18,200103,9,'1,4;3,9;4,14;',5,'2;99;99;2;','吕布的义父。汉灵帝驾崩后，受何进之邀，入洛阳诛杀十常侍，未至，何进已被宦官所杀。\n之后，丁原与董卓发生冲突，董卓得知丁原手下大将吕布唯利是图并未得重用。便遣使挑拨诱使吕布将其杀害。',0,10, },
[100100] = { '董承','',1,2,20,3,1,6200,5300,7200,8600,400,43,37,50,60,17,200114,9,'1,4;3,9;4,14;',5,'2;99;99;2;','汉献帝的心腹。曹操接献帝回许县后，专国弄权，擅作威福，献帝血写诏书密付董承，望董承能诛杀曹操，匡扶汉室。董承于是邀请了刘备、王子服等一批志士共谋杀曹操，不料有家奴因不满，将密谋告知曹操，因此被曹操诛杀全家。',0,10, },
[100101] = { '灵帝','',1,4,35,1,4,8100,9100,8400,6500,1300,117,185,159,94,112,200607,9,'1,12;3,5;4,13;',4,'4;99;99;2;','东汉第十二代皇帝。昏庸无能，对政治毫不关心，实权全部掌握在十常侍的手里。\n在平定黄巾之乱后，在全国一片混乱中逝世。由于生前没有选定继承人，故而使得刘辩和刘协之间争夺皇位。',0,10, },
[100102] = { '王烈','',1,2,15,2,2,5500,6900,5700,2100,1700,36,45,37,14,67,200114,9,'1,5;3,7;4,9;',3,'2;99;99;2;','灵帝时代的名士。德才兼备，讲义气。王烈少时师从陈寔（shí），闻名遐迩。董卓作乱时避乱辽东，并多次拒绝曹操的聘请。',0,10, },
[100103] = { '孔融','',1,2,20,3,1,6000,3800,8300,8700,400,42,27,57,60,17,200211,9,'1,4;3,9;4,14;',5,'2;99;99;2;','孔子的后人，“建安七子”之一。具有过人的才能和广博的学问。性好宾客，喜抨议时政，言辞激烈，后因触怒曹操而为其所杀。',0,10, },
[100104] = { '刘虞','',1,2,15,3,3,6600,5400,6100,8800,200,43,35,40,57,8,200137,9,'1,4;3,9;4,14;',5,'2;99;99;2;','汉光武帝刘秀之子东海恭王刘强之后。镇守幽州时为政宽仁，安抚百姓，深得人心。\n由于与公孙瓒意见不合而产生矛盾，因而进兵攻击公孙瓒，兵败被擒，惨遭杀害。',0,10, },
[100105] = { '王允','',1,2,20,2,1,6300,5900,6600,2700,1900,44,41,46,19,79,200183,9,'1,5;3,7;4,9;',3,'2;99;99;2;','汉朝司徒，在何进死后，无法忍受董卓的残暴行径，利用貂蝉使用“连环计”，借吕布之手诛杀了董卓。\n董卓被诛后，却不愿赦免董卓的旧部，于是导致董卓旧部发动叛乱，最终被杀。',0,10, },
[100106] = { '陶谦','',1,2,15,1,3,2300,5500,6900,5200,1100,15,36,45,34,43,200216,9,'1,8;3,4;4,9;',3,'2;99;99;2;','为参与讨伐董卓的诸侯之一。由于曹操来犯，请求孔融、刘备救援。后来病倒，临终之际将徐州托付给刘备。',0,10, },
[100107] = { '刘表','',1,2,20,1,4,6900,6800,7600,4800,900,48,47,53,34,38,200120,9,'1,8;3,4;4,9;',3,'2;99;99;2;','汉末群雄之一，任荆州牧。为人性多疑忌，立意自守，而无四方之志，后更宠溺后妻蔡氏，使妻族蔡瑁等得权。\n刘表死后，蔡瑁等人废长立幼，奉表次子刘琮为主；曹操南征，刘琮举州投降，荆州遂没。',0,10, },
[100108] = { '禁军','',1,1,15,3,2,5000,6100,5900,7200,100,31,38,37,45,4,200140,9,'1,3;3,8;4,12;',5,'1;99;99;2;','跟随武将的亲卫队，擅长马上作战。',0,10, },
[100109] = { '陈宫','',5,2,20,1,4,5600,7500,8900,4800,900,50,66,79,43,48,200115,11,'1,10;3,5;4,12;',4,'2;99;99;2;','吕布的军师，为人足智多谋。后因曹操杀害边让而与曹操反目，弃曹投靠吕布。吕布战败后，随吕布等一同被曹操所擒。\n白门楼前，曹操劝降陈宫，陈宫不肯屈降，曹操忍痛下令斩首处决陈宫。',0,10, },
[100110] = { '李儒','',5,3,20,1,3,5300,6300,9300,5400,1100,47,81,156,69,76,200610,11,'1,13;3,6;4,15;',4,'3;99;99;2;','董卓军的参谋，为人冷酷残暴。在董卓拥护献帝时，为了断绝后顾之忧，毒杀了先帝弘农王和其母何太后。但在军事方面，具有出色的才能，曾授计徐荣，大败曹军。',0,10, },
[100111] = { '周仓','',5,2,20,2,1,8100,7200,5700,3100,1900,72,64,51,28,101,200098,11,'1,6;3,9;4,12;',4,'2;99;99;2;','为演义中的虚构人物。黄巾军的将军，张宝的部下。在黄巾之乱中，率领黄巾军四处叛乱。\n后来遇到关羽，被关羽的人格魅力所折服，在黄巾军被平定后成为了关羽的部下。',0,10, },
[100112] = { '','',5,2,20,1,4,7200,5600,6700,5300,1000,64,50,59,47,53,200100,11,'1,10;3,5;4,12;',8,'2;99;99;2;','同为关羽部下。两人因饮酒失火，而被关羽怒责。后因惧怕关羽，而投吴，导致关羽兵败被杀。\n刘备伐吴时，二人杀马忠再次投蜀，但刘备认为二人因势急而投降，并非本意，同时恼怒二人背叛关羽，遂将二人凌迟处死。',0,0, },
[100113] = { '李肃','',5,2,20,1,2,7300,7500,5300,4800,1200,65,66,47,43,64,200100,11,'1,10;3,5;4,12;',4,'2;99;99;2;','董卓的部下，和吕布是同乡好友。向董卓提议拉拢吕布，并借吕布之手杀了丁原。之后，和吕布一起参与了王允暗杀董卓的计划。',0,10, },
[100114] = { '公孙瓒','',5,2,25,3,3,8300,8000,6500,8300,500,79,76,62,79,29,200108,11,'1,5;3,12;4,17;',6,'2;99;99;2;','北平太守。与刘备有学友之谊，曾接纳刘备于帐下。在讨伐北方异族的时候，率领白马军团建立不少功绩，被称为白马将军。\n在与袁绍相争中，被袁绍击败，最终被困于自己修建的高楼之中，引火自焚，势力被袁绍吞并。',0,10, },
[100115] = { '高顺','',5,3,30,3,2,8800,8900,4300,9100,1300,146,148,52,137,130,200608,11,'1,6;3,15;4,22;',7,'3;99;99;2;','为人清白有威严，不好酒，所统率的部队精锐非常，号称“陷阵营”，让敌人闻风丧胆。\n早期曾接连击败刘备和夏侯惇。曹操击破吕布后，不愿投降，最后和吕布共同被杀。',0,10, },
[100116] = { '袁术','',5,3,25,2,2,7800,7900,7000,3700,2000,108,109,87,41,166,200609,11,'1,8;3,11;4,15;',4,'3;99;99;2;','大汉名门的袁家子弟，袁绍之弟。作为借给孙策兵马的条件，厚脸皮地“借”来了孙策得到的传国玉玺。\n之后虽然孙策归还了借去的兵马，但袁术却没有归还玉玺，竟然称帝，定国号为“成”。',0,10, },
[100117] = { '张勋','',5,2,25,3,1,6800,7900,5900,8800,600,65,75,56,84,35,200203,11,'1,5;3,12;4,17;',6,'2;99;99;2;','袁术麾下的重要将领，袁术称帝后的大将军。袁术和吕布的和亲破裂后，统帅二十万大军与吕布军交战，但是大败而回。袁术死后归降曹操。',0,10, },
[100118] = { '淳于琼','',5,2,25,2,2,8200,5200,6200,3300,1500,78,50,59,32,86,200097,11,'1,6;3,9;4,12;',4,'2;99;99;2;','袁绍手下的猛将。西园八校尉之一，与张郃、高览等人齐名。在官渡之战时镇守乌巢，遭到曹操的偷袭而惨败，自己也被曹操处斩。',0,10, },
[100119] = { '高览','',5,2,25,2,2,7700,7700,6700,3600,1600,74,74,64,35,92,200110,11,'1,6;3,9;4,12;',4,'2;99;99;2;','与颜良、文丑、张郃并称河间四将，本为袁绍部将，后在官渡之战因遭郭图谗言，与张郃一同投降曹操，官至偏将军、东莱侯。',0,10, },
[100120] = { '沮授','',5,2,20,1,4,4600,6200,8200,3900,1000,41,55,73,35,53,200181,11,'1,10;3,5;4,12;',4,'2;99;99;2;','袁绍的军师。谋略过人，靠着他的计策，袁绍得以一统河北。\n官渡之战时袁绍大败，沮授未及逃走，被曹操所获，因拒降被曹操处死。',0,10, },
[100121] = { '逢纪','',5,2,15,1,2,3600,5100,8000,4600,1000,30,43,67,39,50,200117,11,'1,10;3,5;4,12;',4,'2;99;99;2;','袁绍的军师，袁绍甚为赏识其智谋而重用。袁绍用逢纪之计，胁迫韩馥，得到冀州。\n后袁谭与曹军在黎阳交战，大败。袁谭令逢纪作书，请袁尚再次发兵，但袁尚却不同意，袁谭大怒，立斩逢纪。',0,10, },
[100122] = { '麴义','',5,2,20,1,4,7300,7100,5200,6400,900,65,63,46,57,48,200179,11,'1,10;3,5;4,12;',4,'2;99;99;2;','袁绍部下的将领，能征善战，屡建战功，早年在凉州，精通羌人战法，率领着袁绍的精锐部队。后来由于自恃功高而骄纵不轨，被袁绍所杀。',0,10, },
[100123] = { '张郃','',5,3,25,2,3,8900,8000,7000,3200,1800,123,111,67,40,118,200637,11,'1,8;3,11;4,15;',4,'3;99;99;2;','曹魏“五子良将”之一，在官渡之战中归顺曹操。其戎马一生，以用兵巧变、善列营阵，长于利用地形著称。\n早年随曹操征战四方，屡建战功。曹丕登位后，在街亭大败蜀军，并于江陵破吴，功绩显著。',0,5, },
[100124] = { '南华老仙','',5,2,20,2,3,4300,4600,8800,2400,1700,38,41,78,22,90,200138,11,'1,6;3,9;4,12;',4,'2;99;99;2;','将《太平要术》赠与了到山中采药的张角的神秘人物。',0,10, },
[100125] = { '成公英','',5,2,20,2,2,6300,6500,5600,3000,1900,56,57,50,27,101,200123,11,'1,6;3,9;4,12;',4,'2;99;99;2;','初为韩遂的心腹。为人忠义尽节，韩遂兵败华阴还湟中，部众散去，唯他相随。韩遂死后，降曹操，被委以军师，封列侯。',0,10, },
[100126] = { '祢衡','',5,2,20,2,2,2900,6200,8100,3100,1300,26,55,72,28,69,200663,11,'1,6;3,9;4,12;',4,'2;99;99;2;','拥有敏锐观察力与智谋的文官。但由于其恃才傲物的个性而频繁遭到弹劾。虽然先后投靠曹操和刘表，但均因他的口无遮拦而被放逐。后仕黄祖，因与黄祖言语冲突而被杀。',0,10, },
[100127] = { '韩遂','',5,2,20,3,2,5400,8200,6000,9500,500,48,73,53,84,27,200129,11,'1,5;3,12;4,17;',6,'2;99;99;2;','汉末群雄之一，以诛宦官为名举兵造反，聚众十万，先后败皇甫嵩、张温、董卓、孙坚等名将，后受朝廷招安，拥兵割据一方长达三十余年。\n袁绍、曹操相争之际，马腾、韩遂被钟繇说服，依附于曹操。',0,10, },
[100128] = { '精兵','',5,1,10,3,2,5900,3000,4100,6600,1200,46,24,32,52,56,200165,11,'1,4;3,10;4,15;',6,'1;99;99;2;','群雄阵营精锐骑兵。',0,10, },
[100129] = { '关平','',3,3,25,1,2,8800,8100,6400,4100,1000,117,97,53,50,65,200625,10,'1,11;3,5;4,13;',4,'3;99;99;2;','关羽过五关时收其为义子，从此作为关羽副将，随关羽南征北战，屡立功劳，多与刘封搭档。\n之后逐渐成长为一员大将，曾与庞德大战三十合，不分胜负。关羽败走麦城之际，关平跟随，一同被捕，遭孙权杀害。',0,10, },
[100130] = { '黄忠','',3,3,30,1,4,9000,8500,7600,4800,900,130,123,69,57,64,200611,10,'1,11;3,5;4,13;',4,'3;99;99;2;','“老当益壮”的代言人，蜀国五虎上将之一。在定军山一战中斩杀了曹操部下名将夏侯渊',0,5, },
[100131] = { '赵云','',5,3,30,3,2,9500,7800,6400,8900,300,158,130,77,120,22,200055,11,'1,6;3,15;4,22;',7,'3;99;99;2;','蜀汉“五虎上将”之一。其人姿颜雄伟，勇武过人。先后参加过博望坡之战、长坂坡之战、江南平定战，独自指挥过入川之战、汉水之战、箕谷之战，均屡获奇功。',0,3, },
[100132] = { '马良','',3,2,20,2,2,5200,6000,8500,3100,1600,40,46,66,24,74,200124,10,'1,5;3,8;4,10;',4,'2;99;99;2;','马谡之兄，人称白眉马良。其在五兄弟中最有才干，因此有“马氏五常，白眉最良”的赞誉。\n在出使孙吴，安抚异族方面都有出色表现。',0,10, },
[100133] = { '陈到','',3,2,30,2,1,7400,9000,7600,2900,2000,67,81,69,27,108,200178,10,'1,5;3,8;4,10;',4,'2;99;99;2;','三国时期蜀汉将领，自豫州随刘备，名位亚于赵云，以忠勇著称，统率白毦兵。\n在孔明赴前线和魏交战时，担任守卫吴蜀国境的重任。',0,10, },
[100134] = { '严颜','',3,3,25,1,3,8300,7300,5400,3700,1200,120,97,45,40,69,200657,10,'1,11;3,5;4,13;',4,'3;99;99;2;','原位刘璋部下。擅使硬弓和大刀，有万夫莫敌之勇。据守巴郡，与张飞交战，后为张飞用计俘虏。颜宁死不屈，张飞壮而释之，引为上宾，严颜感概张飞的恩义而归降蜀国。',0,10, },
[100135] = { '法正','',3,2,25,3,2,6500,4600,9200,8800,600,54,38,76,73,30,200100,10,'1,4;3,10;4,15;',5,'2;99;99;2;','原为刘璋部下，刘备围成都时劝说刘璋投降，而后又与刘备进取汉中，献计将曹操大将夏侯渊斩首。\n法正善奇谋，深受刘备信任和敬重，被陈寿称赞为可比魏国的程昱和郭嘉。',0,0, },
[100136] = { '吴兰','',3,2,20,3,1,6500,6900,5000,9600,300,50,53,39,74,14,200199,10,'1,4;3,10;4,15;',5,'2;99;99;2;','刘璋的部将，后降刘备。汉中对曹操的战役中，和雷铜一起被任命攻打下辩，支援马超和张飞，并切断了曹操的补给线，大大打击了曹洪和曹休。\n后被魏将曹洪所击败，败退时为阴平的氐族首领强端所杀。',0,10, },
[100137] = { '雷铜','',3,2,20,2,2,7000,6900,6000,2600,1700,54,53,46,20,79,200238,10,'1,5;3,8;4,10;',4,'2;99;99;2;','本为益州牧刘璋的部将，后归刘备。雷铜虽勇猛，但智谋不足，和张飞守巴西时，因中张郃之埋伏计而被斩杀。',0,10, },
[100138] = { '李严','',3,2,25,2,2,7500,7100,7500,3000,1500,62,59,62,25,75,200195,10,'1,5;3,8;4,10;',4,'2;99;99;2;','蜀国重臣，与诸葛亮同为刘备临终前的托孤之臣。蜀军北伐时，李严运粮不继，假称东吴来犯，使得诸葛亮退兵，但又向后主奏说粮食充足，不知诸葛亮为何退兵。结果被诸葛亮贬为庶民。\n诸葛亮病逝，李严亦因失去复官机会，大哭病死。',0,10, },
[100139] = { '马岱','',3,3,25,3,2,8600,8100,7200,7700,500,125,107,69,83,29,200642,10,'1,5;3,13;4,19;',6,'3;99;99;2;','马超的从弟。早年追随马超大战曹操，反攻陇上，围攻成都，汉中之战等。诸葛亮病逝后受杨仪派遣斩杀了蜀将魏延。曾率领军队出师北伐，被魏将牛金击败而退还。',0,5, },
[100140] = { '马超','',3,3,30,3,2,9700,9200,4200,8500,200,127,133,38,146,13,200623,10,'1,5;3,13;4,19;',6,'3;99;99;2;','蜀国五虎将之一，以勇猛着称，早年随父征战各地。\n曹操诱杀马腾后，马超为报父仇，起兵反曹，在潼关击败大将张郃，数合枪挑李通，杀得曹操割须弃袍，夏侯渊、曹洪、许褚三人的夹攻下，仍然被马超杀至壕边。',0,3, },
[100141] = { '沙摩柯','',3,2,20,1,2,8600,8000,3400,6100,1200,66,62,27,61,56,200101,10,'1,9;3,4;4,10;',4,'2;99;99;2;','五溪蛮夷的首领，刘备伐吴时的援军，战斗中一箭射杀东吴猛将甘宁。后刘备兵败，沙摩柯被东吴大将周泰杀死。',0,10, },
[100142] = { '精兵','',3,1,10,2,2,5400,4800,4400,2100,1500,37,33,30,15,61,200150,10,'1,5;3,7;4,9;',3,'1;99;99;2;','蜀军精锐步兵。',0,10, },
[100143] = { '于禁','',2,2,20,3,1,7800,7600,7100,8400,400,60,59,55,65,19,200104,10,'1,4;3,10;4,15;',5,'2;99;99;2;','曹操手下的老将。战功累累，为魏国五将军之一。\n襄樊之战中，于禁在败给关羽后投降，因而失去了曹操和曹丕对其的信任。归魏后，受到曹丕冷嘲热讽，最终羞愤交加而死。',0,10, },
[100144] = { '乐进','',2,2,25,2,1,6700,8100,7600,3700,1800,56,67,63,31,103,200223,10,'1,5;3,8;4,10;',4,'2;99;99;2;','曹魏“五子良将”之一。以胆识英烈而从曹操，随军多年，南征北讨，战功无数。于官渡之战中，奋勇力战，斩杀了袁绍部将淳于琼。',0,10, },
[100145] = { '夏侯渊','',2,3,30,1,4,8800,8100,6800,5500,700,139,128,71,80,50,200613,10,'1,11;3,5;4,13;',4,'3;99;99;2;','曹操靡下名将。夏侯惇的族弟。几乎参加了曹魏全部重要战役，勇猛异常，在和马超的对战和汉中战役中表现出色。\n汉中平定后被任命驻守汉中，后被黄忠所杀。',0,5, },
[100146] = { '邓忠','',2,2,20,2,2,8100,8400,4600,2500,1500,62,65,36,20,80,200225,10,'1,5;3,8;4,10;',4,'2;99;99;2;','曹魏将领邓艾之子。随父亲在段谷打败姜维。在长城作战时，与姜维激战，不分胜负。\n后遭钟会诬陷，与其父邓艾被押往洛阳，半路被邓艾旧部解救，后被田续追上并杀死。',0,10, },
[100147] = { '曹洪','',2,2,25,3,1,7900,8700,6700,8500,200,66,72,56,71,10,200207,10,'1,4;3,10;4,15;',5,'2;99;99;2;','曹操的族弟。在曹操被董卓追击大败，失去马匹时，曹洪舍命献马并救护曹操，使曹操免于厄难。\n汉中之战时，曹洪与曹休在下辩抵御刘备大军，破斩吴兰、任夔，逼退张飞与马超。',0,10, },
[100148] = { '曹彰','',2,3,25,3,2,8900,7500,4800,8600,300,129,90,40,125,22,200633,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曹操之子。生来好战，喜搏猛虎，勇猛大力，不善文章。自小立志为大将，为曹操所欣赏。\n代北乌桓无（能）臣氐等造反，曹彰引军往讨，大捷而归。后带兵往阳平为曹操助战，曹彰使一条长戟，大败刘封，杀死蜀将吴兰。',0,10, },
[100149] = { '程昱','',2,3,20,2,2,6200,7600,9300,2500,1700,69,76,136,22,102,200614,10,'1,7;3,10;4,13;',4,'3;99;99;2;','曹操的重要参谋之一。由荀彧推荐给曹操。本名程立，因梦中在泰山捧日，更名程昱。\n在仓亭之战中，采用“十面埋伏”的计策大败袁绍。',0,10, },
[100150] = { '荀攸','',2,3,25,2,2,2300,7100,9500,2800,1500,25,68,185,31,108,200634,10,'1,7;3,10;4,13;',4,'3;99;99;2;','荀彧之侄，擅长灵活多变的克敌战术和军事策略，被称为曹操的“谋主”。\n荀攸随讨吕布，定河北，及官渡、赤壁之战，屡出制胜奇谋。',0,10, },
[100151] = { '曹真','',2,2,20,3,2,6900,8000,4000,7700,500,53,62,31,59,23,200218,10,'1,4;3,10;4,15;',5,'2;99;99;2;','其父为曹操招募人马时被州郡所杀，曹操因怜悯曹真少年丧父而待其如亲子一般，因赞赏曹真的勇猛而让他率领虎豹骑。\n曹真在镇守曹魏西北边境时表现突出，魏文帝时期督众将大破羌胡联军，平定河西；魏明帝时期屡次对抗诸葛亮的北伐。',0,10, },
[100152] = { '满宠','',2,2,20,2,1,4300,5600,7800,2800,1600,33,43,60,22,74,200200,10,'1,5;3,8;4,10;',4,'2;99;99;2;','由刘晔推荐给曹操的将领，其人清廉严法，勇而有谋。\n关羽围攻樊城，满宠协助曹仁守城，劝阻了弃城而逃的计划，成功坚持到援军到来。后主要负责对吴的作战，并屡获战功',0,10, },
[100153] = { '曹睿','',2,2,15,1,2,6000,6800,6300,4700,900,44,49,46,34,39,200186,10,'1,9;3,4;4,10;',4,'2;99;99;2;','曹魏第二代皇帝。曹丕和甄皇后之子。在位期间指挥曹真、司马懿等人成功防御了吴、蜀的多次攻伐，并且平定鲜卑，攻灭公孙渊，颇有建树。\n在统治后期，对政治失去了兴趣，生活奢靡，早夭。',0,10, },
[100154] = { '钟会','',2,2,25,2,3,6900,7600,9300,5100,1000,57,63,77,43,50,200236,10,'1,5;3,8;4,10;',4,'2;99;99;2;','钟繇之幼子。自幼才华横溢，上至皇帝、下至群臣都对他非常赏识。之后担任司马昭的心腹，在镇压叛乱时立下功劳。\n之后，由于其有自立的野心，和蜀国的姜维一起谋反，被司马昭看破而杀之。',0,10, },
[100155] = { '精兵','',2,1,10,3,1,5700,5500,6300,6000,300,39,38,43,41,13,200160,10,'1,4;3,9;4,13;',5,'1;99;99;2;','魏军精锐骑兵。',0,10, },
[100156] = { '韩当','',4,3,25,1,3,8200,7600,7300,4600,1100,99,91,79,50,79,200656,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国将领。擅长弓箭、骑术并且臂力过人。历仕孙坚、孙策、孙权三代，随从其征伐四方，功勋卓著，对江东基业的逐渐稳固和吴国的建立有着重要影响。',0,10, },
[100157] = { '甘宁','',4,3,30,1,3,9600,7500,7100,4700,1100,126,98,74,62,78,200624,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国猛将。性格粗暴，年轻时经常为非作歹，行为举止旁若无人。后来熟读诸子而被感化，投靠刘表和黄祖，但均未被重用。\n于是甘宁率步众投奔孙权，破黄祖据楚关，攻曹仁取夷陵，镇益阳拒关羽，守西陵获朱光，率骑袭曹营，战功赫赫。',0,10, },
[100158] = { '黄盖','',4,2,25,2,2,8000,8900,6500,2100,1700,66,74,54,18,85,200178,10,'1,5;3,8;4,10;',4,'2;99;99;2;','历仕孙坚、孙策、孙权三任君主。为人严肃，善于训练士卒，每每征讨，他的部队皆勇猛善战。其在赤壁之战中提议使用苦肉计诈降而大败曹操。',0,10, },
[100159] = { '蒋钦','',4,3,20,1,3,8200,6700,7400,4200,1100,110,67,57,42,59,200641,10,'1,11;3,5;4,13;',4,'3;99;99;2;','其人贵守约，性豁达，弓术精湛。与周泰原为江贼，孙策脱离袁术自立门户时，和周泰一起率众投靠。曾在赤壁之战与周泰、韩当率领水军在三江口踏江破敌。',0,10, },
[100160] = { '张纮','',4,2,20,2,2,5400,6600,7500,3100,1600,42,51,57,24,74,200202,10,'1,5;3,8;4,10;',4,'2;99;99;2;','吴国谋士，和张昭并称为“二张”，是孙策亲自招募的贤士。\n江东平定后，张纮奉命去许都上表。恰缝孙策死去，张纮说以利害，劝使曹操不要加兵江南。之后返回吴国，数度进言孙权，发挥了不小的作用。',0,10, },
[100161] = { '张昭','',4,2,15,1,2,3800,5800,8800,3200,1100,28,42,64,24,48,200121,10,'1,9;3,4;4,10;',4,'2;99;99;2;','孙吴重臣，和张纮并称为“二张”。其人敢于直谏、性格刚直，深受孙策的敬重。孙策死后，张昭遵循孙策的遗嘱辅佐孙权。',0,10, },
[100162] = { '程普','',4,3,25,3,2,8200,8900,7700,8000,700,99,118,74,86,46,200626,10,'1,5;3,13;4,19;',6,'3;99;99;2;','历仕孙坚、孙策、孙权三任君主。是吴国最年长的将军，人们都敬称其为“程公”。\n他曾跟随孙坚讨伐过黄巾、董卓，又助孙策平定江东。孙策死后，他与张昭等人共同辅佐孙权，并讨伐江东境内的山贼，功勋卓著。',0,5, },
[100163] = { '鲁肃','',4,3,20,1,2,5100,6700,9100,4800,1100,45,75,133,54,66,200617,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国军师。因周瑜的推荐而投入孙权旗下。周瑜去世后，鲁肃代周瑜成为水军都督。\n他巧妙利用周瑜的“天下二分之计”，不侵略蜀国，而是与其结盟，一起对抗曹操。',0,10, },
[100164] = { '诸葛瑾','',4,2,15,1,3,5500,6300,8200,3500,1000,40,46,60,26,44,200134,10,'1,9;3,4;4,10;',4,'2;99;99;2;','吴国大臣，诸葛亮之兄，诸葛恪之父。其人胸怀宽广，温厚诚信，深受孙权的信赖，在缓和蜀汉与东吴的关系作出了很大的贡献。在民生、外交、军事各方面都有建树，是吴国出色的文官。',0,10, },
[100165] = { '丁奉','',4,2,25,2,2,8000,7600,7700,2200,1700,66,63,64,19,85,200180,10,'1,5;3,8;4,10;',4,'2;99;99;2;','吴国重要将领。年少时以骁勇为小将，经常奋勇杀敌，屡立功勋。在东兴之战中“雪中奋短兵”，大破进犯东吴的魏军。\n丁奉一生征战，与北方政权从曹操时代打到司马炎时代，侍奉了孙权到孙皓四位吴国君主，见证了三国的盛衰兴亡。',0,10, },
[100166] = { '吕蒙','',4,2,25,3,1,8800,8300,3400,9900,300,73,69,29,82,15,200182,10,'1,4;3,10;4,15;',5,'2;99;99;2;','孙吴重臣。早年果敢有胆，后折节读书，识见精博，终成大将，有“士别三日，更应刮目相待”之语。\n曾在逍遥津之战中奋勇抵抗张辽军追袭，并于濡须数御魏军；又设计袭取荆州，击败关羽，屡获战功。',0,10, },
[100167] = { '精兵','',4,1,10,1,4,5400,3200,5000,3500,1500,37,22,34,24,61,200170,10,'1,8;3,4;4,9;',3,'1;99;99;2;','吴军精锐弓兵。',0,10, },
