[100181] = { '程远志','',5,1,20,2,1,6200,5900,5400,2800,1600,52,50,45,24,80,200140,11,'1,5;3,8;4,10;',4,'1;99;99;2;','黄巾军的将领。黄巾军起义时，程远志与副将邓茂统兵五万进犯涿郡。最后副将邓茂被张飞以丈八蛇矛给刺死，程远志本人也被关羽以青龙偃月刀给劈为两半。',0,10, },
[100182] = { '裴元绍','',5,1,20,3,1,7300,6200,4200,8500,400,61,52,35,71,20,200145,11,'1,4;3,10;4,15;',6,'1;99;99;2;','原黄巾军之武将。黄巾起义失败后，与周仓一同率领残部当了山贼。在关羽过五关时，与周仓一同希望成为关羽的部下，后被留守山中。不久后，因其欲夺偶然路过的赵云之马，反遭讨伐战败身死。',0,10, },
[100183] = { '马元义','',5,1,15,1,2,5400,6500,3800,4900,1000,43,51,30,39,47,200150,11,'1,9;3,4;4,10;',4,'1;99;99;2;','张角的部下，负责筹划起事的准备工作，但遭到张角的弟子唐周告密而被何进逮捕处死。至此张角知道事情已经败露，从而提前起兵发动叛乱。',0,10, },
[100184] = { '李傕&郭汜','',5,2,35,3,3,8500,6600,4500,8500,400,97,76,52,97,28,200128,11,'1,5;3,12;4,17;',6,'2;99;99;2;','本为董卓部将。董卓死后，两人伙同董卓旧将，击败吕布，杀死王允等人，占领长安，把持朝廷大权。之后两人反目，被曹操率军击退。',0,10, },
[100185] = { '许攸','',5,1,15,1,2,4800,3400,7300,4500,1200,38,27,57,36,57,200160,11,'1,9;3,4;4,10;',4,'1;99;99;2;','本为袁绍帐下谋士，官渡之战时其家人因犯法而被收捕，许攸因此背袁投曹，并为曹操设下偷袭袁绍军屯粮之所乌巢的计策，袁绍因此而大败于官渡。\n后许攸随曹操平定冀州，因自恃其功而屡屡口出狂言，终因触怒曹操而被杀。',0,10, },
[100186] = { '审配','',5,2,15,1,4,3100,3500,7600,5100,1200,26,29,63,43,60,200227,11,'1,10;3,5;4,12;',4,'2;99;99;2;','袁绍的军师，为人正直，被委以腹心之任。曹操围邺城时，审配死守数月，终城破被擒，拒不投降，慷慨受死。',0,10, },
[100187] = { '陈琳','',5,1,20,2,1,5600,3500,6800,3200,1800,47,30,57,27,90,200156,11,'1,5;3,8;4,10;',4,'1;99;99;2;','建安七子之一。效力袁绍时，在官渡之战中写了讨曹檄文，骂尽曹操祖宗十八代。但曹操被其文采所折服，非但没有动怒，并且将其招入自己的帐下。',0,10, },
[100188] = { '郭图','',5,1,15,1,4,3100,5100,6600,4400,900,25,40,52,35,43,200161,11,'1,9;3,4;4,10;',4,'1;99;99;2;','袁绍帐下谋士。在袁绍取得冀州上作出了巨大贡献。官渡之战时力主趁机偷袭曹营，在此计失败后为免于责罚而归罪于率军偷袭曹营的张郃、高览，致使二人背袁投曹。',0,10, },
