[100198] = { '糜竺','',3,1,20,2,1,3900,6100,6800,2900,1600,29,45,50,21,70,200171,10,'1,5;3,7;4,9;',3,'1;99;99;2;','原为徐州富商，刘备入主徐州时，与其弟麋芳拒绝曹操的任命而跟随刘备，并在刘备最潦倒之时给予刘备很大的帮助，使他重新振作。\n麋竺雍容大方，敦厚文雅，擅骑射，虽然从未统领军队，但一直被刘备待以上宾之礼。',0,10, },
[100199] = { '伊籍','',3,1,15,1,2,5900,3900,6600,4100,1100,41,27,45,28,45,200163,10,'1,8;3,4;4,9;',3,'1;99;99;2;','本为刘表的幕僚，后在刘备投靠刘表时，被刘备的卓越见识所打动，在刘表死后投靠刘备。\n擅长外交，出使吴国时凭借口舌使孙权折服。后和诸葛亮、法正一起制定了蜀国的法律《蜀科》。',0,10, },
[100200] = { '孙乾','',3,1,20,2,2,5200,5000,8100,2300,1700,38,37,59,17,74,200161,10,'1,5;3,7;4,9;',3,'1;99;99;2;','自徐州跟随刘备，多次作为刘备的使臣，并且在外交上表现出色。刘备入蜀后，孙乾位居糜竺之后。',0,10, },
[100201] = { '张松','',3,2,20,3,1,3400,5200,6900,8300,500,27,40,53,64,23,200205,10,'1,4;3,10;4,15;',5,'2;99;99;2;','刘璋的部下。脸丑且品行不佳，但极富谋略。在出使魏国时受到曹操的冷嘲热讽时，却能一字不差地背诵了曹操的“孟德新书”以示反讽的事件非常有名。\n之后由于谋划将益州献给刘备，事情败露被杀。',0,10, },
[100202] = { '王平','',3,3,20,1,4,7200,8300,7200,5600,1100,72,112,72,50,59,200627,10,'1,11;3,5;4,13;',4,'3;99;99;2;','原属曹操，曹操与刘备争汉中时，得以投降刘备。第一次北伐时与马谡一同守街亭，之后深受诸葛亮的器重，率领蜀汉王牌军队“无当飞军”，多次随诸葛亮北伐。\n诸葛亮死后镇守汉中，曹爽率领十万大军攻汉中时，被王平所击退。',0,10, },
[100203] = { '吴懿','',3,1,20,3,1,7100,6700,5900,8200,200,52,49,43,60,9,200187,10,'1,4;3,9;4,13;',5,'1;99;99;2;','原为刘璋部下，在刘备入蜀时投降。刘备在汉中称王时，娶了吴懿之妹为妻，吴懿最后官至左将军。\n刘备死后，跟随诸葛亮征战各地，屡获战功。',0,10, },
[100204] = { '陈珪＆陈登','',3,2,40,1,4,6100,6800,8300,4500,1100,66,74,90,49,72,200117,10,'1,9;3,4;4,10;',4,'2;99;99;2;','父子二人曾效力曹操，后被刘备的人格魅力所折服，最终叛曹降刘，被刘备誉为当世难得之良才。',0,10, },
[100205] = { '军士','',3,0,15,3,1,4800,2800,3400,8500,300,31,18,22,55,12,200175,10,'1,3;3,8;4,12;',5,'0;99;99;2;','蜀军普通骑兵。',0,10, },
