[100207] = { '军士','',3,0,15,1,3,4800,2800,2900,4000,900,31,18,19,26,35,200177,10,'1,7;3,3;4,8;',3,'0;99;99;2;','蜀军普通弓兵。',0,10, },
[100208] = { '曹昂','',2,1,20,3,1,7000,6200,3200,8300,600,51,45,24,60,27,200165,10,'1,4;3,9;4,13;',5,'1;99;99;2;','曹操的长子。聪明且性情谦和，为曹操所喜爱，二十岁时即举孝廉。\n随曹操出征张绣时，因张绣突然袭击，曹昂为救曹操负责断后，与大将典韦一同战死于宛城。',0,10, },
[100209] = { '李典','',2,2,20,1,4,6500,7800,7800,6500,1000,50,60,60,50,46,200191,10,'1,9;3,4;4,10;',4,'2;99;99;2;','曹操麾下将领。其人深明大义，不与人争功，崇尚学习与高贵儒雅，尊重博学之士，在军中被称为长者。李典有长者之风，官至破虏将军，三十六岁去世。',0,10, },
[100210] = { '曹植','',2,1,15,1,2,3200,4200,8300,4300,1100,22,29,57,30,45,200170,10,'1,8;3,4;4,9;',3,'1;99;99;2;','曹操的三子。在年少时就具有出色的文才而被曹操所欣赏。但由于其散漫任性的性格，在继承人之争上失败。\n之后，在曹丕想要杀害曹植时，曹植作出七步诗使曹丕羞愧难当，成为一段佳话。',0,10, },
[100211] = { '刘晔','',2,1,20,3,1,5700,5400,7700,8200,600,42,40,56,60,27,200188,10,'1,4;3,9;4,13;',5,'1;99;99;2;','光武帝刘秀之子刘延的后裔。刘晔年少知名，人称有佐世之才，是曹操手下举足轻重的谋士，他屡献妙计，对天下形势的发展往往一语中的。刘晔历仕数朝，是曹魏的三朝元老。',0,10, },
[100212] = { '杨修','',2,1,20,1,2,4200,4800,7900,4600,1200,31,35,57,34,53,200146,10,'1,8;3,4;4,9;',3,'1;99;99;2;','为曹操的主簿，其人学问渊博，极聪慧。在汉中攻防战时，偶然听到曹操说了“鸡肋”一词，便自作聪明将曹操的意思理解为撤退。\n于是不等曹操指示而擅自作撤退的准备。曹操嫉妒其才智，且为袁术之甥，虑为后患，因而杀之。',0,10, },
[100213] = { '曹休','',2,1,20,3,2,7900,5800,5400,5100,1100,57,42,40,37,48,200137,10,'1,4;3,9;4,13;',5,'1;99;99;2;','三国曹魏将领，曹操族子。官至大司马，封长平侯。于曹操起兵讨伐董卓时前往投奔，曹操称赞其为“千里驹”。\n汉中之战中，识破张飞计谋，大败吴兰。曹魏建立后，镇守曹魏东线，多次击破吴军，诱降吴将。',0,10, },
[100214] = { '军士','',2,0,15,3,1,3300,4800,2900,8500,500,22,31,19,55,20,200175,10,'1,3;3,8;4,12;',5,'0;99;99;2;','魏军普通骑兵。',0,10, },
