[100217] = { '朱治','',4,2,15,1,3,7000,6200,3600,4400,1000,51,45,26,32,44,200189,10,'1,9;3,4;4,10;',4,'2;99;99;2;','东吴名将朱然之嗣父。早年随从孙坚、孙策征伐，又辅助孙权，稳定江东，功勋卓著。',0,10, },
[100218] = { '凌操','',4,1,20,3,1,6900,7700,6600,7500,300,50,56,48,55,14,200176,10,'1,4;3,9;4,13;',5,'1;99;99;2;','孙策靡下猛将，凌统之父。早年跟随孙氏父子，是水军不可多得的将领。孙权统军后，凌操随其征伐黄祖，被甘宁射杀。',0,10, },
[100219] = { '阚沢','',4,1,20,2,1,3600,3300,7600,2300,1800,27,24,55,17,79,200163,10,'1,5;3,7;4,9;',3,'1;99;99;2;','吴国参谋，其人博学多闻。在赤壁之战时，为了配合周瑜和黄盖的苦肉计，向曹操献诈降书 ，即使被曹操识破苦肉计也面不改色，并以一番妙言使曹操信以为真，是苦肉计中的关键人物。',0,10, },
[100220] = { '虞翻','',4,1,20,1,2,4800,5000,5800,4400,900,35,37,42,32,40,200145,10,'1,8;3,4;4,9;',3,'1;99;99;2;','吴国学者、官员，日南太守虞歆之子。他本是会稽太守王朗部下功曹，后投奔孙策，自此仕于东吴。他于经学颇有造诣，尤其精通《易》学。',0,10, },
[100221] = { '朱桓','',4,3,25,2,1,7500,8600,6500,2800,1600,90,135,70,31,92,200640,10,'1,7;3,10;4,13;',4,'3;99;99;2;','豪门出身，善于谋略的吴国智将。为人高傲，不喜欢为人所驱使，而又善养士卒，轻财重义，与人一见，数十年不忘，死时部下无不哭啼。\n在和曹仁数万大军的作战中，仅以五千人以计谋大败曹仁。',0,10, },
[100222] = { '顾雍','',4,1,20,2,1,5600,3700,6800,2400,1600,41,27,50,18,70,200161,10,'1,5;3,7;4,9;',3,'1;99;99;2;','吴国文官，为相十九年，多进良言，有功于吴。其人善于观察事物，少言语，一旦开口必然一语中的。',0,10, },
[100223] = { '孙静','',4,1,15,1,3,5600,6300,6800,4300,900,39,43,47,30,37,200173,10,'1,8;3,4;4,9;',3,'1;99;99;2;','孙坚之弟。孙坚初起义军时，孙静集合乡里及宗族子弟五六百人作为孙坚的基础队伍。\n孙坚死后，先后负责守卫会稽城和吴郡全局，官至昭仪中郎将。',0,10, },
