[100227] = { '陈群','',2,2,15,1,3,3500,4500,7600,4400,900,26,33,55,32,39,200213,10,'1,9;3,4;4,10;',4,'2;99;99;2;','陈群历仕曹操、曹丕、曹叡三代，以其突出的治世之才，竭忠尽职，为曹魏政权的礼制及其政治制度的建设，做出了突出的贡献。',0,10, },
[100228] = { '李通','',2,2,20,3,1,7500,7200,5100,8200,300,57,56,40,63,14,200229,10,'1,4;3,10;4,15;',5,'2;99;99;2;','早年以游侠闻名于江汝，在率众补充曹操兵源有功拜为阳安都尉，其间不因私而忘公。官渡之战不但不被利诱，在当时许都以南民心不稳，唯李通的阳安犹存。在准备救援曹仁于南郡时病死于途中。',0,10, },
[100229] = { '朱灵','',2,1,20,1,4,5800,3400,3900,3900,900,42,25,29,29,40,200170,10,'1,8;3,4;4,9;',3,'1;99;99;2;','本是袁绍的部下，后来在袁绍的命令下帮助曹操讨伐陶谦，认识到“曹公乃真明君也”，于是投靠曹操。之后，一直辅佐至曹睿一代。',0,10, },
[100230] = { '牛金','',2,1,20,3,1,7100,6000,3400,7900,300,52,44,25,57,14,200140,10,'1,4;3,9;4,13;',5,'1;99;99;2;','初为曹仁部将。周瑜军数万人来攻南郡时，曹仁遣牛金迎战，但对方兵力较多，牛金遂被围困。后被曹仁带兵救出。',0,10, },
[100231] = { '蔡瑁','',2,2,25,2,2,7000,7700,7000,3000,1700,57,64,57,25,85,200234,10,'1,5;3,8;4,10;',4,'2;99;99;2;','久事刘表。刘备依附荆州时，蔡瑁深忌刘备，设计欲于宴会上刺杀，又作反诗嫁祸于刘备，但是都没有成功。\n刘表死后，投降曹操，因其深通养练水军之法，被封为水军大都督，深为周瑜所忌。在赤壁之战前，由于周瑜的离间计，被曹操处决。',0,10, },
[100232] = { '蒋济','',2,2,15,1,2,3900,4800,7200,4400,800,29,35,52,32,35,200128,10,'1,9;3,4;4,10;',4,'2;99;99;2;','曹魏重臣，四朝元老。历仕曹操、曹丕、曹叡、曹芳四代，官至太尉，为曹魏出过不少有价值的建议。后相助司马懿诛杀曹爽。',0,10, },
[100233] = { '曹逵','',2,1,20,1,2,5000,7200,6000,4200,1100,37,53,44,31,48,200141,10,'1,8;3,4;4,9;',3,'1;99;99;2;','在曹丕即位后，曹逵辅助其执政，并在洞浦击败吕范等，是一名多面型人才。\n虽然和曹休关系不佳，但在和吴的战役中看破敌军周鲂的诈降计策，救助了差点中计的曹休。',0,10, },
[100234] = { '王凌','',2,2,20,1,3,5600,6000,4800,4200,700,43,46,37,33,33,200133,10,'1,9;3,4;4,10;',4,'2;99;99;2;','魏国将领、大臣，王允之侄。政治能力出色，担任四洲刺史，受到百姓好评。也擅长作战，在石亭之战时，和贾逵一起救曹休于困境。\n趁司马懿专权之际，想要废除曹芳职位意图谋反，后被人告密给司马懿，计划失败，服毒自尽。',0,10, },
[100235] = { '周旨','',2,1,20,3,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,4;3,9;4,13;',5,'1;99;99;2;','晋国牙将。担任杜预的部将，随军讨伐东吴。率领八百水军进攻夏口，斩杀敌将孙歆、沈莹，立下大功。',0,0, },
[100239] = { '精兵','',2,1,20,2,1,3800,4600,4400,2800,1000,28,34,32,21,44,200176,10,'1,5;3,7;4,9;',3,'1;99;99;2;','魏军精锐步兵。',0,10, },
[100240] = { '吕范','',4,1,20,1,4,6500,6600,6000,5000,1100,47,48,44,37,48,200143,10,'1,8;3,4;4,9;',3,'1;99;99;2;','东吴重臣。随孙策、孙权征伐四方，对稳定江东做出了杰出的贡献，孙权将其比之于吴汉。\n曾为刘备和孙尚香的婚事牵线搭桥，想趁此暗杀刘备，但最后计划未能成功。',0,10, },
[100241] = { '宋谦','',4,2,20,2,1,7000,5400,5000,3200,1600,54,42,39,25,74,200103,10,'1,5;3,8;4,10;',4,'2;99;99;2;','东吴将领。曾于神亭岭一役中与黄盖、韩当等人一同护卫孙策。曾在合肥之战中，救出败于乐进的孙权。夷陵之战中作为陆逊的部属，大破刘备。',0,10, },
[100242] = { '祖茂','',4,1,15,3,1,5600,5000,4200,7300,300,39,34,29,50,13,200155,10,'1,4;3,9;4,13;',5,'1;99;99;2;','孙坚的心腹将领。讨伐董卓的战争中，在孙坚被华雄追杀之时，祖茂带上孙坚的红色头巾来诱敌。\n孙坚成功脱险，祖茂却不幸战死，使得孙坚十分悲伤。',0,10, },
[100243] = { '孙桓','',4,3,20,1,3,8500,8500,8300,4300,1000,85,95,83,43,60,200662,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国将领。与陆逊共拒刘备伐吴。在压倒性兵力的蜀军面前，孙恒虽然被重重包围，但浴血奋战，最后成功撤退。并在刘备败退时，不断切断刘备退路，把刘备逼上绝境。',0,0, },
[100244] = { '陈武','',4,3,20,2,2,8700,7200,4300,2900,1500,97,72,33,29,100,200661,10,'1,7;3,10;4,13;',4,'3;99;99;2;','孙策、孙权靡下猛将。由于陈武仁厚好施，故受到上至孙权，下至乡里的敬爱。其屡建战功，每战皆所向无前，被封为偏将军。\n在合肥战役中，张辽奇袭孙权，陈武战死，孙权对此十分哀痛。',0,0, },
[100245] = { '董袭','',4,3,25,2,1,8800,8500,5000,3900,1600,95,113,48,42,103,200660,10,'1,7;3,10;4,13;',4,'3;99;99;2;','孙吴部将。孙策征战江东时，董袭献上严白虎的人头来降孙策。在赤壁之战、合肥会战时屡获战功。在濡须口之战时，董袭在船上督战，船覆董袭坚守殉职。',0,0, },
[100246] = { '潘璋','',4,2,20,2,1,7700,6700,5500,4200,1500,59,52,43,33,69,200188,10,'1,5;3,8;4,10;',4,'2;99;99;2;','孙权的勇将。在荆州战役中，潘璋跟随吕蒙奇袭烽火台，俘获关羽、关平，并夺得关羽的青龙偃月刀。刘备伐吴期间，更是诱杀老将黄忠。后在夷陵之战中被关兴斩杀。',0,10, },
[100247] = { '孙瑜','',4,1,10,1,4,4200,5200,6800,4100,900,29,36,46,28,37,200177,10,'1,8;3,4;4,9;',3,'1;99;99;2;','孙坚之弟孙静的次子。曾跟随周瑜、孙权等人征战，擅于安抚部下、招降纳顺。\n孙瑜爱读古籍，又请学者马普来为将领官员的子弟讲学，并东吴开始设立学官。',0,10, },
[100248] = { '骆统','',4,1,20,1,2,4000,4800,7000,3900,900,29,35,51,29,40,200170,10,'1,8;3,4;4,9;',3,'1;99;99;2;','侍奉孙权的吴国忠臣。曾在刘备攻吴时，跟着陆逊击退刘备，并在濡须口的战役中击破曹仁，立下战功。',0,10, },
[100249] = { '全琮','',4,1,20,2,1,7300,5200,5000,2200,800,53,38,37,16,35,200140,10,'1,5;3,7;4,9;',3,'1;99;99;2;','吴国名将，孙权的女婿。全琮素有谋略，曾参与多场重要战役的谋划，立下军功，深受孙权的信赖。',0,10, },
[100253] = { '精兵','',4,1,15,2,1,4500,3900,4500,2500,1000,31,27,31,17,41,200177,10,'1,5;3,7;4,9;',3,'1;99;99;2;','吴军精锐步兵。',0,10, },
[100254] = { '孟达','',3,1,20,2,1,5200,5000,5100,2300,1700,38,37,37,17,74,200175,10,'1,5;3,7;4,9;',3,'1;99;99;2;','本为刘璋属下，后降刘备。关羽围樊城、襄阳时因不发兵救关羽而触怒刘备，于是投奔曹魏。此后又欲反曹魏而归蜀汉，事败而死。',0,10, },
[100255] = { '刘封','',3,2,15,3,1,7200,7300,7100,8000,500,52,53,52,57,22,200231,10,'1,4;3,10;4,15;',5,'2;99;99;2;','刘备的义子。有武艺，性格刚猛，气力过人。随赵云、张飞等扫荡西川，颇有战功，而后又统领孟达攻取上庸，深为刘备信任。',0,10, },
[100256] = { '廖化','',3,2,20,3,2,7200,6800,4600,8100,200,56,53,36,62,10,200192,10,'1,4;3,10;4,15;',5,'2;99;99;2;','黄巾军的旧部。在关羽过五关之后成了关羽的得力的部将。关羽死后，奋战在第一线而立下了不少汗马功劳。',0,10, },
[100257] = { '蒋琬','',3,2,20,2,1,6200,4500,8000,2900,1800,48,35,62,23,83,200131,10,'1,5;3,8;4,10;',4,'2;99;99;2;','与诸葛亮、董允、费祎合称“蜀汉四相”。刘备在荆州时归属刘备，在内政方面表现出色。北伐之时在后方支援大军。\n诸葛亮死后，刘禅依诸葛亮遗言加蒋琬为丞相，与费祎一起处理丞相事务。',0,10, },
[100258] = { '费祎','',3,1,20,2,1,3800,4700,6900,2300,1500,28,34,50,17,66,200146,10,'1,5;3,7;4,9;',3,'1;99;99;2;','三国时蜀汉名臣，与诸葛亮、蒋琬、董允并称为蜀汉四相，深得诸葛亮所器重。\n诸葛亮死后，初为后军师，再为尚书令，再迁大将军，执行休养生息的政策，为蜀汉的发展尽心竭力。',0,10, },
[100259] = { '糜芳＆士仁','',3,2,40,2,1,6500,6100,6800,2400,1700,71,66,74,26,111,200100,10,'1,5;3,8;4,10;',8,'2;99;99;2;','使东汉王朝走向灭亡的诸侯。在灵帝末年的十常侍之乱时受大将军何进之召率军进京，旋即掌控朝中大权。其为人残忍嗜杀，倒行逆施，招致群雄联合讨伐。后被其亲信吕布所杀，余部由李傕等人率领。',0,0, },
[100260] = { '','',3,2,25,3,1,7000,7500,6000,9100,300,57,62,50,76,15,200100,10,'1,4;3,10;4,15;',5,'2;99;99;2;','诸葛亮之子。邓艾伐蜀时，他与长子诸葛尚及蜀将张遵等人防御绵竹，后其不听黄崇死守的建议出城与邓艾决战，在交战时阵亡，绵竹也因此失守。',0,0, },
[100264] = { '精兵','',3,1,15,1,3,4400,3800,3600,4300,800,30,26,25,30,33,200175,10,'1,8;3,4;4,9;',3,'1;99;99;2;','蜀军精锐步兵。',0,10, },
[100265] = { '侯成','',5,2,15,3,1,6200,6900,3200,8600,400,52,57,27,72,20,200206,11,'1,5;3,12;4,17;',6,'2;99;99;2;','曾任吕布部将，因酿酒而为吕布所疑。曹操围下邳，侯成等缚陈宫，率众降曹操。',0,10, },
[100266] = { '波才','',5,1,20,3,1,3100,5100,6600,6400,200,26,43,55,54,10,200176,11,'1,4;3,10;4,15;',6,'1;99;99;2;','黄巾军高级将领，统领东方黄巾军部队。曾在黄巾起义初期取得一定战果，但在长社之战被皇甫嵩的火攻击溃。',0,10, },
[100267] = { '胡车儿','',5,1,20,2,1,6600,3600,3200,2600,1600,55,30,27,22,80,200175,11,'1,5;3,8;4,10;',4,'1;99;99;2;','初从张绣，为其心腹猛将，勇冠三军，与贾诩交情甚佳。宛城大战后，张绣投降曹操，曹操爱胡车儿之骁勇，手以黄金与之。\n后胡车儿与张绣串通，盗走典韦的双戟并刺杀典韦，使得典韦在张绣叛乱中战死。',0,10, },
[100268] = { '张绣','',5,3,25,3,1,8500,7800,6200,9000,300,130,97,59,124,20,200096,11,'1,6;3,15;4,22;',7,'3;99;99;2;','初随张济征伐，张济死后与刘表联合。后降曹操，因不满其言行而突袭曹操，复与刘表联合。\n官渡之战前夕，在贾诩的劝诫下，再度投诚曹操。',0,10, },
[100269] = { '魏续','',5,2,20,1,3,6600,5100,3800,4400,900,59,45,34,39,48,200100,11,'1,10;3,5;4,12;',4,'2;99;99;2;','吕布的部下。曾在濮阳之战中，大败曹操，并差点抓住逃跑的曹操。\n后在下邳守城战中被曹操使用水攻，眼看形势不利，于是缚了吕布的军师陈宫，降曹。',0,10, },
[100270] = { '邓茂','',5,1,15,2,2,5600,4600,4000,2200,1500,44,36,32,18,71,200187,11,'1,5;3,8;4,10;',4,'1;99;99;2;','黄巾军将领程志远的副将，随之引兵五万进犯涿郡。在大兴山下，邓茂被张飞一回合刺于马下。',0,10, },
[100271] = { '袁氏兄弟','',5,2,40,3,1,5600,7000,4800,8000,200,70,87,60,100,15,200123,11,'1,5;3,12;4,17;',6,'2;99;99;2;','袁谭、袁熙、袁尚三兄弟，皆为袁绍之子。在袁绍败给曹操并病死后，为了争夺爵位，被曹操利用并相继杀死。',0,10, },
[100272] = { '辛评','',5,1,15,2,1,3100,4800,6400,4900,800,25,38,51,39,38,200150,11,'1,5;3,8;4,10;',4,'1;99;99;2;','原是韩馥部下，后效力于袁绍。袁绍死后，因拥立长子袁谭，与审配等不和。\n后来曹操破邺城，其弟辛毗在城下劝降，审配怒遣手下将辛评全家杀害。',0,10, },
[100276] = { '精兵','',5,1,15,2,1,4400,3200,3400,2900,1000,35,26,27,23,47,200177,11,'1,5;3,8;4,10;',4,'1;99;99;2;','群雄阵营精锐步兵。',0,10, },
[100277] = { '黄巾贼','',5,1,15,2,1,4200,4600,3100,2300,1500,33,36,25,18,71,200165,11,'1,5;3,8;4,10;',4,'1;99;99;2;','黄巾军普通步兵。',0,10, },
[100278] = { '西凉铁骑','',5,0,20,3,1,1000,1000,1000,1000,1000,8,8,8,8,47,200100,11,'1,4;3,9;4,13;',5,'0;99;99;2;','西凉军精锐骑兵。',0,0, },
[100279] = { '黄祖','',1,2,15,1,4,6000,4500,4200,4300,700,39,30,28,28,28,200221,9,'1,8;3,4;4,9;',3,'2;99;99;2;','在与孙坚交战时其部下将孙坚射死，因此与孙家结下仇怨，后来在和孙权的战斗中，被投降孙权的旧部甘宁所杀。',0,10, },
[100280] = { '王子服','',1,1,20,2,1,3200,3000,4800,2600,1400,21,20,32,17,55,200176,9,'1,4;3,6;4,8;',3,'1;99;99;2;','东汉偏将军，受到曹操任用，但是一直忠于汉朝。与董承等受衣带诏共同谋杀曹操，后事发，为曹操所杀。',0,10, },
[100281] = { '许劭','',1,1,20,2,1,3000,3100,6500,2000,1400,20,21,43,14,55,200170,9,'1,4;3,6;4,8;',3,'1;99;99;2;','其看人的眼力过人。对于人物的评价非常准确。\n据说他每月都要对当时人物进行一次品评，人称为“月旦评”。曾经评价曹操为“君清平之奸贼，乱世之英雄”。这句话使得曹操大喜。',0,10, },
[100282] = { '刘琦','',1,1,10,1,2,2900,3700,6200,4100,700,18,23,38,25,26,200150,9,'1,7;3,3;4,8;',3,'1;99;99;2;','荆州牧刘表之子。刘表去世后，被蔡瑁等人所废，而奉表次子刘琮为主。后曹操大军南下荆州，刘琦避走江南。',0,10, },
[100283] = { '曹豹','',1,1,20,1,2,6100,5000,3800,3800,800,40,33,25,25,32,200187,9,'1,7;3,3;4,8;',3,'1;99;99;2;','初为陶谦部下，后仕刘备。其女曹氏为吕布次妻。\n刘备领徐州牧后，与袁术战于淮阴石亭，曹豹与张飞守下邳。豹反刘备，间迎吕布，致使吕布得以自称徐州刺史。',0,10, },
[100284] = { '郑泰','',1,1,20,2,1,4200,4000,6300,1900,1300,28,27,41,13,51,200150,9,'1,4;3,6;4,8;',3,'1;99;99;2;','汉末武将，何进为了讨伐宦官，准备向董卓借兵时候，郑泰认为“董卓此人残忍且野心极大”，反对何进的计策，但何进不予理睬。\n何进死后，郑泰和荀攸一起企图诛杀董卓，但是以失败告终，后逃至袁术帐下。',0,10, },
[100285] = { '刘焉','',1,2,20,2,1,5000,7400,7000,3800,700,35,51,49,27,29,200214,9,'1,5;3,7;4,9;',3,'2;99;99;2;','刘璋之父，汉末群雄之一。在黄巾之乱时，作为幽州太守镇压暴乱，之后任益州牧。利用继承五斗米教的张鲁，令蜀国的势力大幅增加。',0,10, },
[100286] = { '王朗','',1,1,15,1,2,3400,4100,7000,3700,900,21,26,43,23,34,200140,9,'1,7;3,3;4,8;',3,'1;99;99;2;','其人学识渊博，与其子王肃都为经学大家。初仕陶谦。后归顺曹操，并成为极力帮助曹操夺权的得力助手，与华歆等人联手，使曹操得以晋升至魏公、魏王的位子。\n曹操死后则威逼汉献帝禅让帝位与曹丕，成为魏国建立的一大功臣。',0,10, },
[100287] = { '田豫','',1,2,20,1,4,7000,7200,4200,4300,1100,49,50,29,30,46,200193,9,'1,8;3,4;4,9;',3,'2;99;99;2;','初从刘备，因母亲年老回乡。后加入曹魏，常年镇守曹魏北疆，从征代郡乌桓、斩骨进、破轲比能，多有功勋；也曾参与对孙吴的作战，在成山斩杀周贺，于新城击败孙权。',0,10, },
[100288] = { '杨彪','',1,1,20,3,1,5000,4900,6800,8000,1000,33,32,45,53,40,200165,9,'1,3;3,8;4,12;',5,'1;99;99;2;','杨修之父。在董卓害怕袁绍发兵而企图迁都长安时，反对迁都行动，激怒了董卓，被董卓罢官。\n董卓死后官复原职，效力于献帝，为汉王朝的复兴尽了很大的努力。',0,10, },
[100310] = { '邓艾','',2,4,30,2,2,9200,8700,8900,6000,2400,161,153,115,87,188,200100,10,'1,8;3,11;4,15;',4,'4;99;99;2;','邓艾文武全才，深谙兵法，对内政也颇有建树。其多年在曹魏西边战线防备蜀汉姜维。\n公元263年他与钟会分别率军攻打蜀汉，最后他率先进入成都，使得蜀汉灭亡。后因遭到钟会的陷害，被司马昭猜忌而被收押，最后与其子邓忠一起被杀害。',0,0, },
[100311] = { '戏志才','',2,1,20,2,1,6500,7500,9200,2400,1000,47,55,67,18,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','由荀彧推荐给曹操的军师。拥有非凡的才能，在部署军事战略上十分出色，可惜天妒英才，不幸早卒。在他去世后，曹操悲痛的表示失去了可畅谈谋略之人。',0,0, },
[100312] = { '甄皇后','',2,3,20,2,2,7100,6700,6500,3200,1400,71,67,73,32,94,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','初期嫁与袁绍次子袁熙，官渡之战时，袁氏败亡，曹军攻入城内，曹丕见其美艳动人，便纳为己有，后来生下曹睿。在曹丕称帝后被封为皇后。',1,0, },
[100313] = { '贾南风','',2,4,20,1,1,7100,8000,7600,5500,1000,88,119,113,68,67,200100,10,'1,13;3,6;4,15;',4,'4;99;99;2;','西晋时期晋惠帝司马衷的皇后，贾充的女儿。貌丑而性妒，因惠帝懦弱而一度专权，是西晋时期“八王之乱”的始作俑者之一。后死于赵王司马伦之手。',1,0, },
[100314] = { '辛宪英','',2,3,20,2,2,7500,8000,8300,3200,2200,75,80,121,32,147,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','魏国幕僚—辛毗之女，陆抗的大敌—“羊祜的养母”。其人聪朗有才鉴，预言了司马懿的政变以及钟会的谋反。',1,0, },
[100315] = { '孟达','',2,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','本为刘璋属下，后降刘备。关羽围樊城、襄阳时因不发兵救关羽而触怒刘备，于是投奔曹魏。此后又欲反曹魏而归蜀汉，事败而死。',0,0, },
[100316] = { '何晏','',2,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','东汉大将军何进之孙。年少有为，喜爱阅读，尤其中意《道德经》，并在生平留下了许多名作。是玄学的创始人，在文学方面做出诸多贡献，为后世留下了宝贵财富。',0,0, },
[100317] = { '文聘','',2,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','原为刘表的将军，后投诚曹军。守御荆城之际，多次引兵阻遏关羽之师，攻其辎重，烧其战船，立下莫大之功，成为曹操倚为屏障的大将之一，威名远播。',0,0, },
[100318] = { '蔡瑁&张允','',5,2,35,1,4,1000,1000,1000,1000,1000,12,12,12,12,69,200100,11,'1,10;3,5;4,12;',4,'2;99;99;2;','两人均为刘表部下。刘表死后，投降曹操，一起统领水军。因他们深通养练水军之法，深为周瑜所忌。在赤壁之战前，由于周瑜的离间计，被曹操处决。',0,0, },
[100319] = { '钟繇','',2,1,20,1,2,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,8;3,4;4,9;',3,'1;99;99;2;','曹魏的开国功臣，内政之能人，将长安治理得井井有条，得到曹操的称赞，并将其视为可与大汉萧何匹敌的能臣。',0,0, },
[100320] = { '贾华','',4,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','孙权的护卫官。在孙权的妹妹孙尚香与刘备的婚礼上，奉吕范之名暗杀刘备，但计划败露。\n孙尚香之母吴国太大怒，欲将其处以极刑，后得刘备求情才留住性命。',0,0, },
[100321] = { '贺齐','',4,1,20,2,2,7500,5000,5200,2100,1500,55,37,38,16,66,200160,10,'1,5;3,7;4,9;',3,'1;99;99;2;','吴国名将。早年在平定山越的战争中立有大功，又讨平叛乱无数，身经百战，所向披靡，深受孙权器重。后来在与魏国的多次边境争斗中也屡立战功，官至后将军。',0,10, },
[100322] = { '吾粲','',4,2,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,46,200100,10,'1,5;3,8;4,10;',4,'2;99;99;2;','出身低贱，因其才能非凡而得到重用，与陆逊等比肩齐名，一起效力于孙吴。曾抗击曹休，平定山越，功绩显赫。\n后遭孙霸等人的谮毁陷害，最终被关进监狱处死。',0,0, },
[100323] = { '太史享','',4,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','太史慈之子。继承其父之遗志，辅佐孙权。虽然没有其父的武勇，但是拥有强力的政治手腕，为吴国的南方治世做出了卓越的贡献。\n在孙权去世后依然得到吴国的重用，晋升为越骑校尉。',0,0, },
[100324] = { '士燮','',4,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','汉末三国时期割据交州一带的军阀。其人温和内敛，拥有极高的人望。在孙权举兵交州时，为了守护民众，将亲生子作为人质交出投降。其后与孙权保持合作关系，一生致力护卫交州，不让战火蔓延至此。',0,0, },
[100325] = { '孙氏','',4,4,20,2,1,5000,7000,9000,2800,2500,62,105,134,35,166,200100,10,'1,8;3,11;4,15;',4,'4;99;99;2;','孙策之女。在孙策还是袁术之臣时，因为攻击陆康而与陆家对立。孙策死后陆逊投入孙权之下，为了巩固两家关系，孙权将孙氏嫁于陆逊。',1,0, },
[100326] = { '夏侯月姬','',3,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','张飞之妻，夏侯渊的侄女。于采桑时偶遇张飞，并一见钟情，而成为其妻。后生二子二女，其二女皆为蜀汉后主皇后。',1,0, },
[100327] = { '赵累','',3,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','蜀汉大将关羽的部下。在吕蒙率吴军侵略荆州时，他与走投无路的关羽父子共守麦城。赵累为了迷惑敌人，在城里插满旗帜，摆放人偶，只以数骑突破吴国大军。\n但最终关羽兵败，而赵累则被吴将潘璋擒获。',0,0, },
[100328] = { '夏侯霸','',3,3,20,2,2,8200,7900,5600,4000,1800,101,79,50,40,120,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','夏侯渊次子。曹爽被司马懿杀死后，他心中不安，投奔蜀汉，被任命为车骑将军，为主要北伐将领。',0,0, },
[100329] = { '敬哀皇后','',3,3,20,2,1,5200,8500,8200,3400,1800,57,95,91,34,108,200071,10,'1,7;3,10;4,13;',4,'3;99;99;2;','张飞和夏侯月姬之女，为人仁慈心善。虽生于武人之家，却天生体弱多病。她不像父亲，而是有着得天独厚的美貌，在刘禅登基后被封为皇后。',1,5, },
[100330] = { '邓芝','',3,2,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,46,200100,10,'1,5;3,8;4,10;',4,'2;99;99;2;','任官清廉、严谨，有治绩，深受刘备重用。刘备逝世后，奉命出使吴国，成功修复两国关系，并深为孙权所欣赏。',0,0, },
[100331] = { '霍弋','',3,3,20,2,2,7700,8300,6400,3200,1700,77,102,57,32,102,200653,10,'1,7;3,10;4,13;',4,'3;99;99;2;','得到诸葛亮极高评价的蜀国武将。在成都沦陷时，身处绝境中，面对敌将的劝降，他十分冷静的表示“主公还不知是否平安，却要我归降，这违背了忠义之道”，以表达对蜀国的忠诚。\n当他得知主公刘禅平安无事，已经归降后，便也归降于魏国。',0,10, },
[100332] = { '黄皓','',3,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','蜀国的宦官。擅长奉承阿谀，抓人软肋。由于深得刘禅的宠爱，他肆无忌惮滥用权力，并排挤在北伐前线的大将军姜维，导致蜀汉朝政败乱。',0,0, },
[100333] = { '诸葛瞻','',3,1,20,2,1,1000,1000,1000,1000,1000,8,8,8,8,44,200100,10,'1,5;3,7;4,9;',3,'1;99;99;2;','诸葛亮之子。邓艾伐蜀时，他与长子诸葛尚及蜀将张遵等人防御绵竹，后其不听黄崇死守的建议出城与邓艾决战，在交战时阵亡，绵竹也因此失守。',0,0, },
[100334] = { '黄权','',3,3,25,2,2,5600,8800,7800,2700,1400,54,128,84,29,101,200654,10,'1,7;3,10;4,13;',4,'3;99;99;2;','原为刘璋部将，因劝谏刘璋勿引刘备入蜀，而为刘璋所疏远。等到益州平定，黄权闭门不降，刘备亲自前往请他，黄权才归降。\n后来刘备称帝，将征伐东吴，黄权劝谏，但刘备并不听从。刘备在夷陵之战大败后，黄权因吴兵隔断在江北岸，欲归无路，不得已而投降曹魏。',0,10, },
[100335] = { '李恢','',3,1,20,1,4,1000,1000,1000,1000,1000,8,8,8,8,44,200143,10,'1,8;3,4;4,9;',3,'1;99;99;2;','蜀国文官。曾为刘璋属下，后投靠刘备。在平定南中的战争中立下赫赫战功，并于战后积极调配南中物资，有效地支持了蜀汉政府的财政。',0,10, },
[100336] = { '邢道栄','',5,1,20,2,1,1000,1000,1000,1000,1000,9,9,9,9,50,200100,11,'1,5;3,8;4,10;',4,'1;99;99;2;','南方太守—刘度靡下猛将。在刘备来犯时出阵迎击，却中计被捕。后答应做刘备的内应，被刘备放回去后又立刻背叛，最后被赵云打败。',0,0, },
[100337] = { '貂蝉','',5,4,25,2,2,7500,7200,8800,5800,1700,127,133,163,71,173,200603,11,'1,9;3,13;4,17;',5,'4;99;99;2;','【中国古代四大美女之一】。其善歌舞，色伎俱佳，凭借自己的美貌智慧在诸侯争霸的战乱年代，辗转于各诸侯之间。\n她通过王允的安排成为了吕布的妾室，巧施连环计成功诱使吕布诛杀了董卓。',1,3, },
[100338] = { '宋宪','',5,2,20,3,2,6100,3300,4700,8000,100,54,30,42,71,6,200097,11,'1,5;3,12;4,17;',6,'2;99;99;2;','吕布军中的武将。曹操攻吕布，与刘备围下邳城时，宋宪与魏续等执吕布的谋士陈宫，献城投降，最终曹操得以擒杀吕布。',0,10, },
[100339] = { '张燕','',5,2,25,3,1,7100,4200,3400,8800,200,68,40,33,84,12,200100,11,'1,5;3,12;4,17;',6,'2;99;99;2;','黄巾起义时，作为黑山军首领祸乱河北。张燕剽捍，敏捷过人，军中称为“飞燕”。官渡之战时投降曹操，被任命为平北将军。',0,0, },
[100340] = { '于吉','',5,3,20,2,2,2000,3400,9000,3100,1000,18,30,127,36,77,200100,11,'1,8;3,11;4,15;',4,'3;99;99;2;','甚得人心的道士。在吴郡、会稽一带为百姓治病。被孙策以“惑乱人心”的罪名逮捕。孙策表示只要他能唤雨就饶了他，可最终在他唤雨后依然处死了他。',0,0, },
[100341] = { '俄何烧戈','',5,2,20,2,2,7500,5200,3000,2300,1500,66,46,27,21,80,200100,11,'1,6;3,9;4,12;',4,'2;99;99;2;','羌族首领迷当大王帐下武将。他为了响应蜀国姜维的求助，率5万精兵助阵。去劫魏寨时，中了陈泰之计，被郭淮、陈泰包围而不能脱，遂自刎而死。',0,0, },
[100342] = { '张横','',5,1,15,2,1,7100,7600,4200,2900,1500,56,60,33,23,71,200187,11,'1,5;3,8;4,10;',4,'1;99;99;2;','西凉太守韩遂部将，旗本八骑之一。韩遂、马超讨伐曹操时，马超联合韩遂、张横等共十部势力起兵反抗曹操，但被击败，他和程银死于陷坑之中。',0,10, },
[100343] = { '李堪','',5,1,15,2,2,6800,7900,4000,3300,1600,54,62,32,26,76,200177,11,'1,5;3,8;4,10;',4,'1;99;99;2;','西凉太守韩遂部将，旗本八骑之一。曹操设计离间韩、马二人关系时，他曾参与密谋降曹的计划，后被马超追袭，曹将于禁放箭射马超，被马超躲过，反而射中前面的李堪，李堪坠马而死。',0,10, },
[100344] = { '马玩','',5,1,15,3,1,7300,6800,4200,8800,200,57,54,33,69,10,200160,11,'1,4;3,10;4,15;',6,'1;99;99;2;','韩遂手下旗本八骑之一，同马超、韩遂一起起兵反抗曹操。\n后马超中曹操离间之计时，马玩和韩遂等人一起密谋降曹，马超发现后，将其砍杀。',0,10, },
[100345] = { '杨秋','',5,1,20,3,1,7400,6900,3800,8300,300,62,57,32,69,15,200140,11,'1,4;3,10;4,15;',6,'1;99;99;2;','韩遂手下旗本八骑之一，同马超、韩遂一起起兵反抗曹操。后马超中曹操离间之计，与韩遂火拼，杨秋等五将步战围攻马超，不能取胜，被马超砍死二人，杨秋逃窜，投降于曹操，被封为列侯，守渭口。',0,10, },
[100346] = { '候选','',5,1,15,3,1,7500,7700,4700,8500,500,59,61,37,67,24,200155,11,'1,4;3,10;4,15;',6,'1;99;99;2;','韩遂手下旗本八骑之一，同马超、韩遂一起起兵反抗曹操，遭遇与杨秋类似，最后降曹获封官爵。',0,10, },
[100347] = { '程银','',5,1,15,3,1,6100,7400,5000,8600,200,48,57,40,68,10,200177,11,'1,4;3,10;4,15;',6,'1;99;99;2;','西凉太守韩遂部将，旗本八骑之一。马超兴兵报父仇，他随同韩遂一道出兵。后和韩遂、侯选、程银、李堪、张横、梁兴、杨秋等一起参与密谋降曹，却被马超发现，最终死于陷坑之中。',0,10, },
[100348] = { '成宜','',5,1,15,3,1,7800,6000,4400,8100,400,61,47,35,64,19,200155,11,'1,4;3,10;4,15;',6,'1;99;99;2;','韩遂手下旗本八骑之一，同马超、韩遂一起起兵反抗曹操，随马超等人夜袭曹操营寨，率三十骑往前哨探，侵入中军，被伏兵包围，为夏侯渊所杀。',0,10, },
[100349] = { '刘璋','',5,2,20,2,1,1000,1000,1000,1000,1000,9,9,9,9,53,200100,11,'1,6;3,9;4,12;',4,'2;99;99;2;','继父亲刘焉担任益州牧，为人懦弱多疑。曹操来袭时，听信手下张松、法正之言，迎刘备入益州抵抗曹操。不料此举乃引狼入室，被刘备反手攻击，法正又为刘备内应，刘璋不得已而投降。',0,0, },
[100350] = { '张鲁','',5,1,20,1,4,1000,1000,1000,1000,1000,9,9,9,9,50,200100,11,'1,9;3,4;4,10;',4,'1;99;99;2;','为五斗米道的第三代天师，在杀张修后继续在汉中一带传播五斗米道，并自称“师君”。雄据汉中近三十年，后投降曹操。',0,0, },
[100351] = { '张任','',5,3,30,1,4,8700,8900,7600,5800,900,118,134,79,70,56,200630,11,'1,13;3,6;4,15;',4,'3;99;99;2;','刘璋靡下猛将，忠勇著称。刘备攻打刘璋时，张任与诸将引兵对抗，并在落凤坡埋伏射杀庞统。\n后诸葛亮用计擒获张任，张任宁死不降，遂被处斩。',0,10, },
[100352] = { '梁兴','',5,1,15,2,1,5500,3600,3200,3000,1500,44,29,26,24,71,200100,11,'1,5;3,8;4,10;',4,'1;99;99;2;','韩遂手下旗本八骑之一，同马超、韩遂一起起兵反抗曹操。后马超中曹操离间之计，与韩遂火拼，梁兴等五将步战围攻马超，不能取胜，随后被马超砍杀。',0,0, },
[100353] = { '纪灵','',5,2,20,3,1,1000,1000,1000,1000,1000,9,9,9,9,53,200100,11,'1,5;3,12;4,17;',6,'2;99;99;2;','袁术的部下。在与刘备军的战斗中，与关羽大打30回合不分胜负。但是在保护投靠袁绍的袁术时，遭到刘备的奇袭，被张飞所杀。',0,0, },
[100354] = { '司马徽','',1,3,20,2,2,1000,3600,9200,2900,1600,6,25,102,26,67,200100,9,'1,6;3,9;4,12;',4,'3;99;99;2;','被称为水镜先生，因为人预测前途而闻名。刘备被蔡瑁追击逃亡时，司马徽曾收留他留宿一夜。随后告诉了他“卧龙”、“凤雏”之名，并表示只要得到其中一人，就能获得天下。',0,0, },
[100355] = { '蔡夫人','',1,3,15,2,2,2400,3000,8300,2900,1400,18,23,78,28,79,200100,9,'1,6;3,9;4,12;',4,'3;99;99;2;','刘表后妻、刘琮生母、蔡瑁之姐。心肠恶毒，因先主劝刘表立长子琦为嗣，深恨之，数次欲加害于琦。后表亡，与蔡瑁等共排表长子刘琦，奉刘琮为嗣，寻降曹操。',1,0, },
[100356] = { '王粲','',1,2,20,2,1,1000,1000,1000,1000,1000,6,6,6,6,42,200100,9,'1,5;3,7;4,9;',3,'2;99;99;2;','建安七子之一，为著名学者蔡邕所赏识。初仕刘表，刘表病逝后，其子刘琮举州投降，王粲也归顺曹操，深得曹氏父子信赖，赐爵关内侯。',0,0, },
[100357] = { '刘焉','',1,3,20,1,4,6100,8200,7700,4200,1000,61,82,77,42,54,200100,9,'1,10;3,5;4,12;',4,'3;99;99;2;','刘璋之父，汉末群雄之一。在黄巾之乱时，作为幽州太守镇压暴乱，之后任益州牧。利用继承五斗米教的张鲁，令蜀地的势力大幅增加。',0,0, },
[100358] = { '张姬','',3,4,25,1,3,9400,8100,2200,5300,700,177,142,21,86,45,200094,10,'1,13;3,6;4,15;',4,'4;99;99;2;','张飞之女。在姐姐敬哀皇后死后，成为了蜀国的皇后。与姐姐一样貌美如花。\n与羸弱的姐姐不同，她继承了其父的顽强，在刘禅被魏国降服后，依然与他生活在洛阳。',1,10, },
[100359] = { '袁姬','',1,3,20,1,2,1000,1000,1000,1000,1000,10,10,10,10,54,200100,9,'1,10;3,5;4,12;',4,'3;99;99;2;','袁术之女。在袁术被曹操打败后，投靠其父旧部庐江太守刘勋。由于自小富裕奢侈，因此个性骄纵任性。\n在刘勋被孙策打败后，成为了孙权的侧室。',1,0, },
[100360] = { '武安国','',1,2,20,2,1,1000,1000,1000,1000,1000,6,6,6,6,42,200100,9,'1,5;3,7;4,9;',3,'2;99;99;2;','孔融的手下，一身怪力，用铁槌做武器。\n在与董卓的战斗中，单挑镇守虎牢关的吕布数十回合，后被吕布斩断手臂。',0,0, },
[100361] = { '亲卫','',5,2,15,3,1,4300,4500,4700,8000,200,36,38,39,67,10,200141,11,'1,5;3,12;4,17;',6,'2;99;99;2;','群雄武将亲卫骑兵。',0,10, },
