[100372] = { '妲己','',5,5,20,3,2,8600,7100,8800,7600,1000,176,146,180,156,123,200100,11,'1,7;3,19;4,28;',8,'5;99;99;2;','【中国古代四大妖姬之一】殷朝末期祸乱纣王的妖姬，是难得一见的绝世美女。\n纣王沉迷于妲己的美色，荒理朝政，对她言听计从，到了“妲己之所誉贵之，妲己之所憎诛之”的地步，使商朝灭亡，最后被周武王所杀。',1,0, },
[100374] = { '妺喜','',5,5,20,2,1,1000,1000,1000,1000,1000,21,21,21,21,123,200100,11,'1,10;3,14;4,19;',5,'5;99;99;2;','【中国古代四大妖姬之一】夏朝最后一位君主夏桀的王后。夏桀在位时，发动大军攻打有施氏，有施兵败求和，并得到妺喜。夏桀得到妺喜后，更加荒淫无度。\n妺喜有三个癖好：一是笑看人们在规模大到可以划船的酒池里饮酒；二是笑听撕裂绢帛的声音；三是喜欢穿戴男人的官帽。',1,0, },
[100376] = { '褒姒','',5,5,20,2,1,1000,1000,1000,1000,1000,21,21,21,21,123,200100,11,'1,10;3,14;4,19;',5,'5;99;99;2;','【中国古代四大妖姬之一】周幽王攻打褒国，褒国兵败，献出褒姒乞降。周幽王爱之如掌上明珠，之后更是废黜申后，而立褒姒为王后；褒姒冷艳少笑，周幽王为博其一笑，烽火戏诸侯。最终西周灭亡，褒姒亦遭劫掳，从此下落不明。',1,0, },
[100377] = { '骊姬','',5,5,20,1,1,1000,1000,1000,1000,1000,21,21,21,21,123,200100,11,'1,16;3,7;4,19;',5,'5;99;99;2;','【中国古代四大妖姬之一】春秋时代骊戎国君的女儿，有姿色，攻心计。被晋献公虏入晋国，成为晋献公的妃子。她使计离间了献公与申生、重耳、夷吾父子兄弟之间的感情，并设计杀死了太子申生，制造了"骊姬倾晋"的事件。',1,0, },
[100378] = { '伏寿','',1,3,20,1,2,7600,5000,7100,4200,600,76,50,101,42,33,200604,9,'1,10;3,5;4,12;',4,'3;99;99;2;','伏寿、即伏皇后，伏完之女，汉献帝的皇后。因伏完参与谋曹操事败，受到牵连，遭曹操杀害。',1,5, },
[100379] = { '杜氏','',1,3,20,2,2,3200,7800,6100,2700,1700,32,103,61,27,92,200605,9,'1,6;3,9;4,12;',4,'3;99;99;2;','吕布麾下秦宜禄之妻，魏骠骑将军秦朗之母。吕布殒命前，关羽曾向曹操讨要杜氏，曹操不允，后曹操自纳之，并将杜氏之子秦朗视为己子。',1,5, },
[100396] = { '法正','',5,2,20,2,1,4300,7500,8900,2600,1400,38,66,79,23,74,200118,11,'1,6;3,9;4,12;',4,'2;99;99;2;','原为刘璋部下，刘备围成都时劝说刘璋投降，而后又与刘备进取汉中，献计将曹操大将夏侯渊斩首。\n法正善奇谋，深受刘备信任和敬重，被陈寿称赞为可比魏国的程昱和郭嘉。',0,10, },
[100398] = { '关兴＆张苞','',3,3,40,2,1,8600,7700,5700,2500,1900,149,134,62,36,179,200606,10,'1,7;3,10;4,13;',4,'3;99;99;2;','关兴为关羽之子，张苞为张飞之子。与其父辈一样义结金兰，并称“小关张”，在蜀军中的表现相当活跃。',0,10, },
[100402] = { '','',5,1,15,2,1,7800,3300,3500,3100,1500,61,26,28,25,71,200100,11,'1,5;3,8;4,10;',8,'1;99;99;2;','大贤良师张角的二弟，自称地公将军。和张角、张良一起发动了黄巾军起义。\n据说会使用妖术，使得皇甫嵩和朱儁数次陷入苦战。最后被朱儁的部下严政所杀。',0,0, },
[100414] = { '少帝','',1,2,20,2,2,4400,6000,5600,3900,1400,31,42,39,27,57,200185,9,'1,5;3,7;4,9;',3,'2;99;99;2;','为灵帝和何皇后之子，汉献帝刘协之兄。即位后不久遭遇何进和十常侍两大敌阵营的火拼，被迫出宫。\n回宫后又受制于以“勤王”为名进京的董卓，并被胁迫自尽。',0,10, },
[100418] = { '左慈','',2,3,20,2,2,1100,1600,9400,2200,1700,9,13,115,22,102,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','神秘的仙人，曾说刘备将夺取天下而激怒曹操。曹操于是命人逮捕了数百与左慈容貌相同的人。\n这些人被斩首时从头部冒出的青烟变成了驾鹤的左慈，左慈预言曹操的死期之后消失。',0,0, },
[100419] = { '夏侯惇','',2,3,20,2,2,8700,8900,6900,1000,1800,97,99,53,10,120,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','从曹操白手起家时就跟随的勇将。平时清廉谨慎，但一到战场上就如鬼神般勇猛。在讨伐吕布时被箭射中左眼，仍然勇猛难挡。\n多次为曹操镇守后方，屡获功绩，官拜大将军。',0,0, },
[100420] = { '夏侯渊','',2,3,20,3,2,8800,9200,5400,9100,200,98,103,42,101,12,200613,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曹操靡下名将。夏侯惇的族弟。几乎参加了曹魏全部重要战役，勇猛异常，在和马超的对战和汉中战役中表现出色。\n汉中平定后被任命驻守汉中，后被黄忠所杀。',0,1, },
[100421] = { '徐晃','',2,3,25,3,2,9300,8400,6400,7500,400,123,111,53,90,23,200628,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曹魏名将。在曹操手下多立功勋，参与官渡、赤壁、关中征伐、汉中征伐等几次重大战役。樊城之战中徐晃作为曹仁的援军击败关羽，因于此役中治军严整而被曹操称赞“有周亚夫之风”。',0,10, },
[100423] = { '徐夫人','',4,3,20,2,1,1000,1000,1000,1000,1000,12,12,12,12,67,200100,10,'1,7;3,10;4,13;',4,'3;99;99;2;','孙坚三子孙翊的妻子，天生聪慧，精通占卜。\n某日，孙翊要举行酒宴。徐氏为其卜卦，认为此去凶多吉少，劝孙翊改日再设宴，但孙翊不听，最后被刺杀。事后徐夫人成功报仇。',1,0, },
[100424] = { '小乔&大乔','',4,4,35,2,2,8000,5000,8800,3100,3000,114,88,155,50,288,200652,10,'1,8;3,11;4,15;',4,'4;99;99;2;','小乔、大乔分别为周瑜、孙策之妻。两人并称为“江东二乔”，是三国时期世人皆知的绝色美女，均受到世人的追捧。',1,3, },
[100426] = { '凌统','',4,3,25,1,2,8200,8500,4100,4900,1000,109,113,34,59,65,200629,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国名将。凌操之子。少有名盛，为人有国士之风，多次战役中表现出色，官至偏将军。\n凌统因父亲凌操被甘宁所杀而一直仇视甘宁，但在濡须口与乐进交战时得甘宁所救，从此便尽释前嫌，与甘宁结为生死之交。',0,10, },
[100427] = { '鲁肃','',4,3,20,1,2,1000,1000,1000,1000,1000,12,12,12,12,60,200100,10,'1,11;3,5;4,13;',4,'3;99;99;2;','吴国军师。因周瑜的推荐而投入孙权旗下。周瑜去世后，鲁肃代周瑜成为水军都督。\n他巧妙利用周瑜的“天下二分之计”，不侵略蜀国，而是与其结盟，一起对抗曹操。',0,0, },
[100428] = { '','',3,3,20,1,4,1000,1000,1000,1000,1000,12,12,12,12,60,200100,10,'1,11;3,5;4,13;',4,'3;99;99;2;','“老当益壮”的代言人，蜀国五虎上将之一。在定军山一战中斩杀了曹操部下名将夏侯渊',0,0, },
[100429] = { '简雍','',3,2,20,2,2,4200,3300,7600,2700,1700,33,26,59,21,79,200100,10,'1,5;3,8;4,10;',4,'2;99;99;2;','简雍擅于辩论、议事，官拜昭德将军，地位次于麋竺。其人性情简单直接、不拘小节。年少时便与刘备相识，后跟随刘备奔走。常作为谈客，往来使命，刘备围成都时，简雍劝说刘璋投降。',0,0, },
[100430] = { '张宝','',5,3,20,1,4,7700,8700,8800,5000,900,78,111,113,57,69,200050,11,'1,13;3,6;4,15;',4,'3;99;99;2;','大贤良师张角的二弟，自称地公将军。和张角、张良一起发动了黄巾军起义。\n据说会使用妖术，使得皇甫嵩和朱儁数次陷入苦战。最后被朱儁的部下，严政所杀。',0,10, },
[100431] = { '邹','',5,4,20,2,1,8200,8100,4000,5000,1700,141,139,40,71,130,200100,11,'1,9;3,13;4,17;',5,'4;99;99;2;','张济之妻，张绣之婶。张绣投降曹操后，操侄曹安民引夫人见操，操见夫人美貌，而强纳之。',1,0, },
[100432] = { '献帝','',1,3,20,2,2,7100,7200,7300,2600,1400,71,72,73,24,76,200100,9,'1,6;3,9;4,12;',4,'3;99;99;2;','汉灵帝刘宏和王美人所生之子。董卓废汉少帝刘辩后，立刘协为皇帝。董卓被诛后，曹操控制了刘协，挟天子以令诸侯。\n曹操死后，刘协被曹丕控制，随后被迫禅让于曹丕。',0,0, },
[100433] = { '荀攸','',1,3,20,1,4,1000,1000,1000,1000,1000,10,10,10,10,54,200100,9,'1,10;3,5;4,12;',4,'3;99;99;2;','荀彧之侄，擅长灵活多变的克敌战术和军事策略，被称为曹操的“谋主”。\n荀攸随讨吕布，定河北，及官渡、赤壁之战，屡出制胜奇谋。',0,0, },
[100434] = { '曹操','',2,5,20,3,1,1000,1000,1000,1000,1000,18,18,18,18,107,200100,10,'1,7;3,17;4,25;',7,'5;99;99;2;','三国中曹魏政权的缔造者。其文武皆通，被誉为乱世之奸雄。\n以汉天子的名义征讨四方，对内消灭二袁、吕布、刘表、马超、韩遂等割据势力，对外降服南匈奴、乌桓、鲜卑等，统一了中国北方，并实行一系列政策恢复经济生产和社会秩序，奠定了曹魏立国的基础。',0,0, },
[100435] = { '夏侯渊','',2,4,30,3,3,9100,9000,6600,10000,1000,200,185,76,204,69,200655,10,'1,6;3,15;4,22;',7,'4;99;99;2;','曹操靡下名将。夏侯惇的族弟。几乎参加了曹魏全部重要战役，勇猛异常，在和马超的对战和汉中战役中表现出色。\n汉中平定后被任命驻守汉中，后被黄忠所杀。',0,10, },
[100436] = { '郭淮','',2,3,20,3,1,8500,8500,7700,8100,300,95,95,77,81,16,200100,10,'1,5;3,13;4,19;',6,'3;99;99;2;','曹操手下的重要将领，夏侯惇的族弟，几乎参加了魏国的全部重要战役，勇猛异常，屡建战功。',0,0, },
[100437] = { '荀彧&荀攸','',2,4,40,2,2,3600,8500,9800,2200,1800,57,194,276,39,169,200100,10,'1,8;3,11;4,15;',4,'4;99;99;2;','叔侄二人均效力于曹魏，且深受曹操重用。两人均具有经天纬地之才，在军事、政事方面均作出了杰出的贡献。',0,0, },
[100438] = { '孙权','',4,5,20,1,1,1000,1000,1000,1000,1000,18,18,18,18,107,200100,10,'1,15;3,7;4,17;',5,'5;99;99;2;','自幼文武双全，早年随父兄征战天下。后继承孙坚与孙策的遗志，统领江东。在曹丕与刘备各自称帝时，被魏文帝曹丕封为吴王，建立吴国。之后脱离魏国，并自称吴帝。',0,0, },
[100439] = { '黄盖','',4,3,30,2,2,8400,8700,6800,2900,1700,99,149,71,31,120,200616,10,'1,7;3,10;4,13;',4,'3;99;99;2;','历仕孙坚、孙策、孙权三任君主。为人严肃，善于训练士卒，每每征讨，他的部队皆勇猛善战。其在赤壁之战中提议使用苦肉计诈降而大败曹操。',0,10, },
[100440] = { '','',4,2,20,1,4,1000,1000,1000,1000,1000,8,8,8,8,46,200100,10,'1,9;3,4;4,10;',4,'2;99;99;2;','原为刘繇部下，后被孙策收降，自此太史慈为孙氏大将，助其扫荡江东。其弓马熟练，箭法精良。后在合肥之战中战死。',0,0, },
[100441] = { '刘备','',3,4,45,2,2,9000,8800,8000,4000,900,227,223,202,86,105,200100,10,'1,8;3,11;4,15;',4,'4;99;99;2;','西汉中山靖王之后，三国时期蜀汉开国皇帝。为人弘毅宽厚，知人待士，百折不挠。与张飞、关羽桃园结义，征战四方，终成霸业。',0,0, },
[100442] = { '黄忠','',3,4,30,1,4,9400,8900,5600,4000,1200,193,209,56,70,105,200100,10,'1,13;3,6;4,15;',4,'4;99;99;2;','“老当益壮”的代言人，蜀国五虎上将之一。在定军山一战中斩杀了曹操部下名将夏侯渊',0,0, },
[100443] = { '法正','',3,4,30,1,2,6500,8000,9500,3700,1100,113,104,209,65,96,200100,10,'1,13;3,6;4,15;',4,'4;99;99;2;','原为刘璋部下，刘备围成都时劝说刘璋投降，而后又与刘备进取汉中，献计将曹操大将夏侯渊斩首。\n法正善奇谋，深受刘备信任和敬重，被陈寿称赞为可比魏国的程昱和郭嘉。',0,0, },
[100445] = { '庞德','',5,3,25,3,1,9300,7400,7100,9100,800,142,92,68,139,46,200100,11,'1,6;3,15;4,22;',7,'3;99;99;2;','曾是马超的心腹，不过被曹操打败后投诚魏国。\n之后成为一方统帅与蜀国作战，为表决心，甚至将棺材抬上战场，在与关羽作战时被擒，并拒绝归顺蜀国，最终殒身殉节。',0,0, },
[100446] = { '','',5,3,20,2,2,4500,7100,9300,3300,1400,46,82,144,38,85,200100,11,'1,8;3,11;4,15;',4,'3;99;99;2;','董卓军的参谋，为人冷酷残暴。在董卓拥护献帝时，为了断绝后顾之忧，毒杀了先帝弘农王和其母何太后。但在军事方面，具有出色的才能，曾授计徐荣，大败曹军。',0,0, },
[100448] = { '','',1,4,25,3,1,1000,1000,1000,1000,1000,15,15,15,15,87,200100,9,'1,5;3,13;4,20;',6,'4;99;99;2;','曹魏“五子良将”之一，在官渡之战中归顺曹操。其戎马一生，以用兵巧变、善列营阵，长于利用地形著称。\n早年随曹操征战四方，屡建战功。曹丕登位后，在街亭大败蜀军，并于江陵破吴，功绩显著。',0,0, },
[100449] = { '夏侯惇','',2,5,45,3,1,1000,1000,1000,1000,1000,28,28,28,28,168,200100,10,'1,7;3,17;4,25;',7,'5;99;99;2;','从曹操白手起家时就跟随的勇将。平时清廉谨慎，但一到战场上就如鬼神般勇猛。在讨伐吕布时被箭射中左眼，仍然勇猛难挡。\n多次为曹操镇守后方，屡获功绩，官拜大将军。',0,0, },
[100450] = { '孙策','',4,5,45,3,1,1000,1000,1000,1000,1000,28,28,28,28,168,200100,10,'1,7;3,17;4,25;',7,'5;99;99;2;','人称“江东小霸王”。为继承父亲孙坚的遗业而屈事袁术，后脱离袁术，统一江东。在一次狩猎中为刺客所伤，不久后身亡。',0,0, },
